/**
 * Advice Engine - Client-side implementation matching the Python AdviceEngine
 * Processes plans.json trigger rules against user metrics
 */

import { Plan } from '@/components/results/cards/PlanActionCard';

// ============================================
// TYPES
// ============================================

interface Threshold {
  operator: '<' | '>';
  value: number;
}

interface TriggerRules {
  metrics: string[];
  condition: 'OR' | 'AND';
  thresholds: Record<string, Threshold>;
}

interface PlanContent {
  description: string;
  cost_min: number;
  cost_max: number;
  time_min?: string;
  time_max?: string;
  risks?: string;
  citations?: string[];
  tags?: string[];
}

interface RawPlan {
  id: string;
  title: string;
  trigger_rules: TriggerRules;
  content: PlanContent;
}

interface TriggerResult {
  metric: string;
  value: number;
  threshold: number;
  operator: '<' | '>';
}

