    30→}
    31→
    32→interface MeasurementCardProps {
    33→  title: string;
    34→  value: number;
    35→  unit: string;
    36→  score: number;
    37→  idealRange: { min: number; max: number };
    38→  description: string;
    39→  rating: 'excellent' | 'good' | 'average' | 'below_average';
    40→}
    41→
    42→function MeasurementCard({
    43→  title,
    44→  value,
    45→  unit,
    46→  score,
    47→  idealRange,
    48→  description,
    49→  rating,
    50→}: MeasurementCardProps) {
    51→  const ratingConfig = {
    52→    excellent: { color: '#10b981', label: 'Excellent', icon: Star },
    53→    good: { color: '#0891b2', label: 'Good', icon: TrendingUp },
    54→    average: { color: '#eab308', label: 'Average', icon: Minus },
    55→    below_average: { color: '#f97316', label: 'Below Avg', icon: TrendingDown },
    56→  };
    57→
    58→  const config = ratingConfig[rating];
    59→  const Icon = config.icon;
    60→
    61→  const isInRange = value >= idealRange.min && value <= idealRange.max;
    62→
    63→  return (
    64→    <motion.div
    65→      initial={{ opacity: 0, y: 10 }}
    66→      animate={{ opacity: 1, y: 0 }}
    67→      className="bg-black rounded-xl border border-neutral-800 p-4 hover:border-neutral-700 transition-colors"
    68→    >
    69→      <div className="flex items-start justify-between mb-3">
    70→        <div>
    71→          <h4 className="text-sm font-semibold text-white">{title}</h4>
    72→          <p className="text-xs text-neutral-500 mt-0.5">{description}</p>
    73→        </div>
    74→        <div
    75→          className="flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium"
    76→          style={{
    77→            backgroundColor: `${config.color}15`,
    78→            color: config.color,
    79→          }}
    80→        >
    81→          <Icon className="w-3 h-3" />
    82→          {config.label}
    83→        </div>
    84→      </div>
    85→
    86→      <div className="flex items-end justify-between">
    87→        <div>
    88→          <span className="text-2xl font-bold text-white">
    89→            {typeof value === 'number' ? value.toFixed(2) : value}
    90→          </span>
    91→          <span className="text-sm text-neutral-500 ml-1">{unit}</span>
    92→        </div>
    93→        <div className="text-right">
    94→          <div className="text-xs text-neutral-500 mb-1">
    95→            Ideal: {idealRange.min.toFixed(1)} - {idealRange.max.toFixed(1)}
    96→          </div>
    97→          <div
    98→            className="text-xs font-medium"
    99→            style={{ color: isInRange ? '#10b981' : '#f97316' }}
   100→          >
   101→            {isInRange ? 'In Range' : value < idealRange.min ? 'Below Ideal' : 'Above Ideal'}
   102→          </div>
   103→        </div>
   104→      </div>
   105→
   106→      {/* Score bar */}
   107→      <div className="mt-3 h-1.5 bg-neutral-800 rounded-full overflow-hidden">
   108→        <motion.div
   109→          className="h-full rounded-full"
   110→          style={{ backgroundColor: config.color }}
   111→          initial={{ width: 0 }}
   112→          animate={{ width: `${Math.min(score, 100)}%` }}
   113→          transition={{ duration: 0.8 }}
   114→        />
   115→      </div>
   116→      <div className="mt-1 text-right">
   117→        <span className="text-xs font-medium" style={{ color: config.color }}>
   118→          Score: {score.toFixed(0)}/100
   119→        </span>
   120→      </div>
   121→    </motion.div>
   122→  );
   123→}
   124→
