'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { ArrowLeft } from 'lucide-react';

// FaceIQ exact ethnicity options
const ETHNICITY_OPTIONS = [
  { id: 'white', label: 'White / Caucasian' },
  { id: 'black', label: 'Black / African descent' },
  { id: 'asian', label: 'Asian / East Asian' },
  { id: 'hispanic', label: 'Hispanic / Latino' },
  { id: 'middle-eastern', label: 'Middle Eastern / North African' },
  { id: 'indian', label: 'Indian / South Asian' },
  { id: 'other', label: 'Other' },
] as const;

type EthnicityId = typeof ETHNICITY_OPTIONS[number]['id'];

export default function EthnicityPage() {
  const router = useRouter();
  const [selectedEthnicity, setSelectedEthnicity] = useState<EthnicityId | null>(null);

  const handleSelect = (ethnicity: EthnicityId) => {
    setSelectedEthnicity(ethnicity);
  };

  const handleContinue = () => {
    if (selectedEthnicity) {
      // Store ethnicity and navigate to upload
      localStorage.setItem('ethnicity', selectedEthnicity);
      router.push('/upload');
    }
  };

  const handleBack = () => {
    router.push('/gender');
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-black px-4">
      <div className="w-full max-w-sm">
        {/* Back Button */}
        <button
          onClick={handleBack}
          className="absolute top-6 left-6 flex items-center gap-2 text-neutral-400 hover:text-white transition-colors text-sm"
        >
          <ArrowLeft className="w-4 h-4" />
          Back
        </button>

        {/* Header */}
        <div className="mb-10">
          <div className="flex justify-center mb-6">
            {/* Logo */}
            <div className="h-8 w-8 rounded bg-[#00f3ff]/20 flex items-center justify-center">
              <span className="text-[#00f3ff] text-sm font-bold">L</span>
            </div>
          </div>
          <h1 className="text-2xl font-semibold tracking-tight text-center text-white mb-2">
            Select Ethnicity
          </h1>
          <p className="text-sm text-neutral-400 text-center">
            This helps us provide accurate analysis
          </p>
        </div>

        {/* Ethnicity Options */}
        <div className="space-y-3">
          {ETHNICITY_OPTIONS.map((option) => (
            <button
              key={option.id}
              onClick={() => handleSelect(option.id)}
              className={`
                w-full h-14 rounded-xl border-2
                flex items-center justify-between px-5
                transition-all duration-200 cursor-pointer
                ${selectedEthnicity === option.id
                  ? 'border-[#00f3ff] bg-[#00f3ff]/5'
                  : 'border-neutral-800 bg-black hover:border-neutral-700'
                }
              `}
            >
              <span className={`
                font-medium transition-colors duration-200
                ${selectedEthnicity === option.id ? 'text-white' : 'text-neutral-300'}
              `}>
                {option.label}
              </span>
              {/* Radio indicator */}
              <div className={`
                w-5 h-5 rounded-full border-2 flex items-center justify-center
                transition-all duration-200
                ${selectedEthnicity === option.id
                  ? 'border-[#00f3ff] bg-[#00f3ff]'
                  : 'border-neutral-600 bg-transparent'
                }
              `}>
                {selectedEthnicity === option.id && (
                  <div className="w-2 h-2 rounded-full bg-black" />
                )}
              </div>
            </button>
          ))}
        </div>

        {/* Continue Button */}
        <button
          onClick={handleContinue}
          disabled={!selectedEthnicity}
          className={`
            w-full h-12 mt-6 rounded-xl font-medium text-sm
            transition-all duration-200
            ${selectedEthnicity
              ? 'bg-[#00f3ff] text-black hover:shadow-[0_0_20px_rgba(0,243,255,0.3)] cursor-pointer'
              : 'bg-neutral-800 text-neutral-500 cursor-not-allowed'
            }
          `}
        >
          Continue
        </button>
      </div>
    </div>
  );
}

