     1→'use client';
     2→
     3→import React, { createContext, useContext, useState, ReactNode } from 'react';
     4→
     5→export type EthnicityOption =
     6→  | 'white'
     7→  | 'black'
     8→  | 'asian'
     9→  | 'south-asian'
    10→  | 'hispanic'
    11→  | 'middle-eastern'
    12→  | 'pacific-islander'
    13→  | 'native-american'
    14→  | 'mixed';
    15→
    16→export const ethnicityLabels: Record<EthnicityOption, string> = {
    17→  'white': 'White / Caucasian',
    18→  'black': 'Black / African',
    19→  'asian': 'East Asian',
    20→  'south-asian': 'South Asian',
    21→  'hispanic': 'Hispanic / Latino',
    22→  'middle-eastern': 'Middle Eastern',
    23→  'pacific-islander': 'Pacific Islander',
    24→  'native-american': 'Native American',
    25→  'mixed': 'Mixed / Other',
    26→};
    27→
    28→interface EthnicityContextType {
    29→  ethnicities: EthnicityOption[];
    30→  toggleEthnicity: (ethnicity: EthnicityOption) => void;
    31→  clearEthnicities: () => void;
    32→}
    33→
    34→const EthnicityContext = createContext<EthnicityContextType | undefined>(undefined);
    35→
    36→export function EthnicityProvider({ children }: { children: ReactNode }) {
    37→  const [ethnicities, setEthnicities] = useState<EthnicityOption[]>([]);
    38→
    39→  const toggleEthnicity = (ethnicity: EthnicityOption) => {
    40→    setEthnicities(prev =>
