-- Migration 015: Add forum activity fields to users table
-- These fields are computed/cached for display on user profiles

-- Add forum activity count fields
ALTER TABLE users ADD COLUMN IF NOT EXISTS forum_posts_count INTEGER DEFAULT 0;
ALTER TABLE users ADD COLUMN IF NOT EXISTS forum_comments_count INTEGER DEFAULT 0;
ALTER TABLE users ADD COLUMN IF NOT EXISTS forum_karma INTEGER DEFAULT 0;

-- Create index for efficient karma-based queries (leaderboard, top contributors)
CREATE INDEX IF NOT EXISTS idx_users_forum_karma ON users (forum_karma DESC) WHERE is_banned = FALSE;

-- Comment explaining the fields
COMMENT ON COLUMN users.forum_posts_count IS 'Cached count of non-deleted forum posts by this user';
COMMENT ON COLUMN users.forum_comments_count IS 'Cached count of non-deleted forum comments by this user';
COMMENT ON COLUMN users.forum_karma IS 'Total karma points: sum of all votes received on posts and comments';

-- Optional: Backfill existing data (run this manually after migration)
-- UPDATE users u SET
--   forum_posts_count = (SELECT COUNT(*) FROM forum_posts p WHERE p.author_id = u.id AND p.is_deleted = FALSE),
--   forum_comments_count = (SELECT COUNT(*) FROM forum_comments c WHERE c.author_id = u.id AND c.is_deleted = FALSE),
--   forum_karma = (
--     SELECT COALESCE(SUM(vote_count), 0)
--     FROM (
--       SELECT vote_count FROM forum_posts WHERE author_id = u.id AND is_deleted = FALSE
--       UNION ALL
--       SELECT vote_count FROM forum_comments WHERE author_id = u.id AND is_deleted = FALSE
--     ) votes
--   );

