/**
 * Forum Types - Matches backend Pydantic schemas
 */

// === ENUMS ===

export type VoteType = 'up' | 'down';

export type TargetType = 'post' | 'comment';

export type ReportReason =
  | 'spam'
  | 'harassment'
  | 'misinformation'
  | 'off_topic'
  | 'inappropriate'
  | 'other';

export type SortOrder = 'hot' | 'new' | 'top';

// === SUB-FORUM ===

export interface SubForum {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  displayOrder: number;
  postCount: number;
}

// === CATEGORY ===

export interface Category {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  displayOrder: number;
  postCount: number;
  subForums: SubForum[];
}

export interface CategoryListItem {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  displayOrder: number;
  postCount: number;
}

// === POST ===

export interface PostAuthor {
  id: string;
  username: string;
}

export interface Post {
  id: string;
  title: string;
  content: string;
  subForumId: string;
  subForumSlug: string;
  categorySlug: string;
  author: PostAuthor;
  isPinned: boolean;
  isGuide: boolean;
  voteCount: number;
  commentCount: number;
  userVote: VoteType | null;
  createdAt: string;
  updatedAt: string;
}

export interface PostListItem {
  id: string;
  title: string;
  contentPreview: string;
  subForumSlug: string;
  categorySlug: string;
  author: PostAuthor;
  isPinned: boolean;
  isGuide: boolean;
  voteCount: number;
  commentCount: number;
  userVote: VoteType | null;
  createdAt: string;
}

export interface PostListResponse {
  posts: PostListItem[];
  totalCount: number;
  hasMore: boolean;
}

export interface PostCreate {
  title: string;
  content: string;
  subForumId: string;
}

export interface PostUpdate {
  title?: string;
  content?: string;
}

// === COMMENT ===

export interface Comment {
  id: string;
  content: string;
  postId: string;
  author: PostAuthor;
  parentId: string | null;
  voteCount: number;
  userVote: VoteType | null;
  depth: number;
  replies: Comment[];
  createdAt: string;
  updatedAt: string;
}

export interface CommentCreate {
  content: string;
  parentId?: string;
}

export interface CommentUpdate {
  content: string;
}

// === VOTE ===

export interface VoteRequest {
  voteType: VoteType;
}

export interface VoteResponse {
  success: boolean;
  newVoteCount: number;
  userVote: VoteType | null;
}

// === REPORT ===

export interface ReportCreate {
  targetType: TargetType;
  targetId: string;
  reason: ReportReason;
  details?: string;
}

export interface Report {
  id: string;
  targetType: TargetType;
  targetId: string;
  reason: ReportReason;
  status: string;
  createdAt: string;
}

// === GUIDES SECTION ===

export interface GuideSection {
  category: CategoryListItem;
  guides: PostListItem[];
}

// === RECOMMENDED FORUMS ===

export interface RecommendedForum {
  category: CategoryListItem;
  matchedFlaws: string[];
  priority: number;
}

// === SEARCH ===

export type SearchResultType = 'post' | 'comment';

export interface ForumSearchResult {
  id: string;
  type: SearchResultType;
  title: string | null;  // Only for posts
  contentPreview: string;
  postId: string | null;  // Only for comments
  postTitle: string | null;  // Only for comments
  author: PostAuthor;
  voteCount: number;
  categorySlug: string;
  subForumSlug: string;
  createdAt: string;
}

export interface ForumSearchResponse {
  results: ForumSearchResult[];
  totalCount: number;
  query: string;
  searchType: string;
  hasMore: boolean;
}

// === USER PROFILE ===

export interface ForumUserProfile {
  id: string;
  username: string;
  forumPostsCount: number;
  forumCommentsCount: number;
  forumKarma: number;
  memberSince: string;
}

export interface UserPostItem {
  id: string;
  title: string;
  contentPreview: string;
  subForumSlug: string;
  categorySlug: string;
  isPinned: boolean;
  isGuide: boolean;
  voteCount: number;
  commentCount: number;
  userVote: VoteType | null;
  createdAt: string;
}

export interface UserPostListResponse {
  posts: UserPostItem[];
  totalCount: number;
  hasMore: boolean;
}

export interface UserCommentItem {
  id: string;
  content: string;
  postId: string;
  postTitle: string;
  categorySlug: string;
  voteCount: number;
  userVote: VoteType | null;
  createdAt: string;
}

export interface UserCommentListResponse {
  comments: UserCommentItem[];
  totalCount: number;
  hasMore: boolean;
}

// === BOOKMARK TYPES ===

export interface BookmarkToggleResponse {
  isBookmarked: boolean;
  message: string;
}

export interface BookmarkStatus {
  postId: string;
  isBookmarked: boolean;
  bookmarkedAt: string | null;
}

export interface BookmarkedPostsResponse {
  posts: PostListItem[];
  totalCount: number;
  hasMore: boolean;
}

// === ADMIN/MODERATION TYPES ===

export type ReportStatusEnum = 'pending' | 'reviewed' | 'resolved' | 'dismissed';

export type ActionTaken = 'content_removed' | 'user_warned' | 'user_banned' | 'no_action';

export interface ReportDetail {
  id: string;
  targetType: TargetType;
  targetId: string;
  reason: ReportReason;
  details: string | null;
  status: ReportStatusEnum;
  reporterId: string;
  reporterUsername: string;
  reviewedBy: string | null;
  reviewedAt: string | null;
  resolutionNotes: string | null;
  actionTaken: ActionTaken | null;
  createdAt: string;
  // Denormalized content for preview
  contentPreview: string | null;
  contentAuthorId: string | null;
  contentAuthorUsername: string | null;
}

export interface AdminReportListResponse {
  reports: ReportDetail[];
  totalCount: number;
  hasMore: boolean;
}

export interface ResolveReportRequest {
  status: ReportStatusEnum;
  resolutionNotes?: string;
  actionTaken?: ActionTaken;
}

export interface ModerationStats {
  pendingReports: number;
  resolvedToday: number;
  resolvedThisWeek: number;
  totalReports: number;
  reportsByReason: Record<string, number>;
  reportsByStatus: Record<string, number>;
}

export interface BanUserRequest {
  reason?: string;
}

export interface BanUserResponse {
  userId: string;
  username: string;
  isBanned: boolean;
  message: string;
}

export interface BannedUser {
  id: string;
  username: string;
  email: string;
  isBanned: boolean;
  createdAt: string | null;
  updatedAt: string | null;
}

export interface BannedUsersResponse {
  users: BannedUser[];
  totalCount: number;
  hasMore: boolean;
}

'use client';

import Link from 'next/link';
import { User, MessageSquare, ArrowUpCircle, Calendar } from 'lucide-react';
import { ForumUserProfile } from '@/types/forum';
import { formatDistanceToNow } from '@/lib/utils';

interface UserProfileCardProps {
  profile: ForumUserProfile;
  variant?: 'full' | 'compact';
}

/**
 * Reusable user profile card component
 * - Full variant: Shows complete profile with karma, post/comment counts
 * - Compact variant: Minimal display for sidebar/inline use
 */
export function UserProfileCard({ profile, variant = 'full' }: UserProfileCardProps) {
  if (variant === 'compact') {
    return (
      <Link
        href={`/forum/user/${profile.id}`}
        className="flex items-center gap-3 p-3 rounded-xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all group"
      >
        <div className="w-10 h-10 rounded-full bg-gradient-to-br from-cyan-500/20 to-purple-500/20 flex items-center justify-center border border-white/10">
          <User size={18} className="text-cyan-400" />
        </div>
        <div className="flex-1 min-w-0">
          <p className="text-sm font-bold text-white group-hover:text-cyan-400 transition-colors truncate">
            u/{profile.username}
          </p>
          <p className="text-[10px] text-neutral-500">
            {profile.forumKarma.toLocaleString()} karma
          </p>
        </div>
      </Link>
    );
  }

  return (
    <div className="rounded-2xl bg-neutral-900/30 border border-white/5 overflow-hidden">
      {/* Header with gradient */}
      <div className="h-24 bg-gradient-to-br from-cyan-500/10 via-purple-500/10 to-pink-500/10 relative">
        <div className="absolute -bottom-8 left-6">
          <div className="w-16 h-16 rounded-full bg-neutral-900 border-4 border-neutral-900 flex items-center justify-center">
            <div className="w-14 h-14 rounded-full bg-gradient-to-br from-cyan-500/20 to-purple-500/20 flex items-center justify-center">
              <User size={28} className="text-cyan-400" />
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="px-6 pt-12 pb-6">
        {/* Username */}
        <h2 className="text-xl font-black text-white mb-1">
          u/{profile.username}
        </h2>

        {/* Member since */}
        <div className="flex items-center gap-1.5 text-neutral-500 text-xs mb-6">
          <Calendar size={12} />
          <span>Member since {formatDistanceToNow(profile.memberSince)}</span>
        </div>

        {/* Stats grid */}
        <div className="grid grid-cols-3 gap-4">
          {/* Karma */}
          <div className="text-center p-4 rounded-xl bg-neutral-900/50 border border-white/5">
            <div className="flex items-center justify-center gap-1.5 mb-2">
              <ArrowUpCircle size={16} className="text-cyan-400" />
            </div>
            <p className="text-lg font-black text-white">
              {profile.forumKarma.toLocaleString()}
            </p>
            <p className="text-[10px] font-medium uppercase tracking-widest text-neutral-500">
              Karma
            </p>
          </div>

          {/* Posts */}
          <div className="text-center p-4 rounded-xl bg-neutral-900/50 border border-white/5">
            <div className="flex items-center justify-center gap-1.5 mb-2">
              <MessageSquare size={16} className="text-purple-400" />
            </div>
            <p className="text-lg font-black text-white">
              {profile.forumPostsCount.toLocaleString()}
            </p>
            <p className="text-[10px] font-medium uppercase tracking-widest text-neutral-500">
              Posts
            </p>
          </div>

          {/* Comments */}
          <div className="text-center p-4 rounded-xl bg-neutral-900/50 border border-white/5">
            <div className="flex items-center justify-center gap-1.5 mb-2">
              <MessageSquare size={16} className="text-emerald-400" />
            </div>
            <p className="text-lg font-black text-white">
              {profile.forumCommentsCount.toLocaleString()}
            </p>
            <p className="text-[10px] font-medium uppercase tracking-widest text-neutral-500">
              Comments
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

/**
 * Inline username link that navigates to user profile
 */
interface UsernameLinkProps {
  userId: string;
  username: string;
  className?: string;
}

export function UsernameLink({ userId, username, className = '' }: UsernameLinkProps) {
  return (
    <Link
      href={`/forum/user/${userId}`}
      className={`hover:text-cyan-400 transition-colors ${className}`}
    >
      u/{username}
    </Link>
  );
}

