'use client';

import { Award, Star, Crown, Gem, Shield } from 'lucide-react';
import {
  KarmaTier,
  getKarmaTier,
  getKarmaTierColor,
  getKarmaTierBgColor,
  getKarmaTierLabel,
} from '@/types/forum';

interface KarmaBadgeProps {
  karma: number;
  variant?: 'badge' | 'inline' | 'compact';
  showNumber?: boolean;
}

/**
 * Get the icon component for a karma tier
 */
function getTierIcon(tier: KarmaTier, size: number = 12) {
  switch (tier) {
    case 'diamond':
      return <Gem size={size} />;
    case 'platinum':
      return <Crown size={size} />;
    case 'gold':
      return <Award size={size} />;
    case 'silver':
      return <Star size={size} />;
    case 'bronze':
      return <Shield size={size} />;
    default:
      return <Shield size={size} />;
  }
}

/**
 * Karma badge component displaying user's karma tier
 *
 * Variants:
 * - badge: Full badge with tier label and optional karma number
 * - inline: Compact inline display with icon and karma
 * - compact: Just the icon with tier color
 */
export function KarmaBadge({
  karma,
  variant = 'badge',
  showNumber = false,
}: KarmaBadgeProps) {
  const tier = getKarmaTier(karma);
  const colorClass = getKarmaTierColor(tier);
  const bgClass = getKarmaTierBgColor(tier);
  const label = getKarmaTierLabel(tier);

  if (variant === 'compact') {
    return (
      <span className={`inline-flex items-center ${colorClass}`} title={`${label} - ${karma.toLocaleString()} karma`}>
        {getTierIcon(tier, 10)}
      </span>
    );
  }

  if (variant === 'inline') {
    return (
      <span
        className={`inline-flex items-center gap-1 ${colorClass}`}
        title={`${label} - ${karma.toLocaleString()} karma`}
      >
        {getTierIcon(tier, 10)}
        <span className="text-[9px] font-bold">{karma.toLocaleString()}</span>
      </span>
    );
  }

  // Default: badge variant
  return (
    <span
      className={`inline-flex items-center gap-1.5 px-2 py-0.5 rounded-md border ${bgClass} ${colorClass}`}
      title={`${karma.toLocaleString()} karma`}
    >
      {getTierIcon(tier, 12)}
      <span className="text-[9px] font-black uppercase tracking-wider">{label}</span>
      {showNumber && (
        <span className="text-[9px] font-medium opacity-75">
          ({karma.toLocaleString()})
        </span>
      )}
    </span>
  );
}

/**
 * Karma display for user profile cards
 */
interface KarmaDisplayProps {
  karma: number;
  className?: string;
}

export function KarmaDisplay({ karma, className = '' }: KarmaDisplayProps) {
  const tier = getKarmaTier(karma);
  const colorClass = getKarmaTierColor(tier);
  const label = getKarmaTierLabel(tier);

  return (
    <div className={`flex flex-col items-center ${className}`}>
      <div className={`flex items-center gap-1.5 ${colorClass}`}>
        {getTierIcon(tier, 20)}
        <span className="text-lg font-black">{karma.toLocaleString()}</span>
      </div>
      <span className={`text-[9px] font-bold uppercase tracking-widest ${colorClass} opacity-75`}>
        {label}
      </span>
    </div>
  );
}

/**
 * Karma leaderboard entry component
 */
interface KarmaLeaderboardEntryProps {
  rank: number;
  username: string;
  userId: string;
  karma: number;
  postsCount: number;
  commentsCount: number;
}

export function KarmaLeaderboardEntry({
  rank,
  username,
  userId,
  karma,
  postsCount,
  commentsCount,
}: KarmaLeaderboardEntryProps) {
  const tier = getKarmaTier(karma);
  const colorClass = getKarmaTierColor(tier);
  const bgClass = getKarmaTierBgColor(tier);

  // Special styling for top 3
  const getRankStyle = () => {
    if (rank === 1) return 'text-amber-400 bg-amber-500/10 border-amber-500/30';
    if (rank === 2) return 'text-neutral-300 bg-neutral-400/10 border-neutral-400/30';
    if (rank === 3) return 'text-amber-600 bg-amber-700/10 border-amber-700/30';
    return 'text-neutral-500 bg-neutral-800/50 border-neutral-700/30';
  };

  return (
    <a
      href={`/forum/user/${userId}`}
      className={`flex items-center gap-4 p-4 rounded-xl border ${bgClass} hover:border-opacity-50 transition-all group`}
    >
      {/* Rank */}
      <div
        className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-black border ${getRankStyle()}`}
      >
        {rank}
      </div>

      {/* User info */}
      <div className="flex-1 min-w-0">
        <div className="flex items-center gap-2">
          <span className="font-bold text-white group-hover:text-cyan-400 transition-colors truncate">
            u/{username}
          </span>
          <KarmaBadge karma={karma} variant="compact" />
        </div>
        <div className="flex items-center gap-3 text-[10px] text-neutral-500 mt-0.5">
          <span>{postsCount} posts</span>
          <span>{commentsCount} comments</span>
        </div>
      </div>

      {/* Karma */}
      <div className={`text-right ${colorClass}`}>
        <div className="text-lg font-black">{karma.toLocaleString()}</div>
        <div className="text-[9px] uppercase tracking-wider opacity-75">karma</div>
      </div>
    </a>
  );
}

