'use client';

import { useState, useEffect, useCallback } from 'react';
import Link from 'next/link';
import { MessageSquare, Share2, Bookmark, BookmarkCheck, MoreHorizontal, Pin, Sparkles, Check, Flag, Loader2 } from 'lucide-react';
import { PostListItem, VoteType, getKarmaTier, getKarmaTierColor } from '@/types/forum';
import { formatDistanceToNow } from '@/lib/utils';
import { VoteButtons } from './VoteButtons';
import { api } from '@/lib/api';
import { useBookmarks } from '@/contexts/ForumContext';
import { KarmaBadge } from './KarmaBadge';

interface PostCardProps {
  post: PostListItem;
  onVote?: (postId: string, voteType: VoteType) => void;
  showSubForum?: boolean;
  onReport?: (postId: string) => void;
}

export function PostCard({ post, onVote, showSubForum = false, onReport }: PostCardProps) {
  const bookmarkContext = useBookmarks();
  const [localIsSaved, setLocalIsSaved] = useState(false);
  const [isBookmarking, setIsBookmarking] = useState(false);
  const [copied, setCopied] = useState(false);
  const [showMenu, setShowMenu] = useState(false);

  // Determine if bookmarked based on context or local state
  const isSaved = bookmarkContext
    ? bookmarkContext.isBookmarked(post.id)
    : localIsSaved;

  // Fetch bookmark status on mount if not using global context
  useEffect(() => {
    if (!bookmarkContext) {
      // Only fetch if user is logged in (token exists)
      const token = typeof window !== 'undefined' ? localStorage.getItem('auth_token') : null;
      if (token) {
        api.getBookmarkStatus(post.id)
          .then(status => setLocalIsSaved(status.isBookmarked))
          .catch(() => setLocalIsSaved(false));
      }
    }
  }, [post.id, bookmarkContext]);

  const handleShare = async () => {
    const url = `${window.location.origin}/forum/post/${post.id}`;
    try {
      await navigator.clipboard.writeText(url);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch {
      // Fallback for older browsers
      const input = document.createElement('input');
      input.value = url;
      document.body.appendChild(input);
      input.select();
      document.execCommand('copy');
      document.body.removeChild(input);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  const handleSave = useCallback(async () => {
    // Check if user is logged in
    const token = typeof window !== 'undefined' ? localStorage.getItem('auth_token') : null;
    if (!token) {
      // Redirect to login if not authenticated
      window.location.href = '/login?redirect=' + encodeURIComponent(window.location.pathname);
      return;
    }

    setIsBookmarking(true);
    try {
      if (bookmarkContext) {
        // Use global context
        await bookmarkContext.toggleBookmark(post.id);
      } else {
        // Use local state with direct API call
        const result = await api.toggleBookmark(post.id);
        setLocalIsSaved(result.isBookmarked);
      }
    } catch (err) {
      console.error('Failed to toggle bookmark:', err);
    } finally {
      setIsBookmarking(false);
    }
  }, [post.id, bookmarkContext]);

  const handleReport = () => {
    setShowMenu(false);
    onReport?.(post.id);
  };

  return (
    <article className="group relative rounded-2xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all overflow-hidden">
      <div className="flex">
        {/* Vote column */}
        <div className="w-12 bg-neutral-900/50 border-r border-white/5 flex flex-col items-center py-4">
          <VoteButtons
            voteCount={post.voteCount}
            userVote={post.userVote}
            onVote={(voteType) => onVote?.(post.id, voteType)}
            size="sm"
          />
        </div>

        {/* Content */}
        <div className="flex-1 p-4 min-w-0">
          {/* Meta line */}
          <div className="flex items-center flex-wrap gap-2 mb-3">
            {post.isPinned && (
              <span className="flex items-center gap-1 px-2 py-0.5 rounded-md bg-cyan-500/10 border border-cyan-500/20 text-[9px] font-black uppercase tracking-wider text-cyan-400">
                <Pin size={10} />
                Pinned
              </span>
            )}
            {post.isGuide && (
              <span className="flex items-center gap-1 px-2 py-0.5 rounded-md bg-green-500/10 border border-green-500/20 text-[9px] font-black uppercase tracking-wider text-green-400">
                <Sparkles size={10} />
                Guide
              </span>
            )}
            {showSubForum && (
              <Link
                href={`/forum/${post.categorySlug}`}
                className="text-[10px] font-black uppercase tracking-widest text-cyan-400 hover:text-cyan-300"
              >
                {post.subForumSlug}
              </Link>
            )}
            <span className="flex items-center gap-1.5 text-[10px] font-medium text-neutral-600">
              by{' '}
              <Link
                href={`/forum/user/${post.author.id}`}
                className="text-neutral-400 hover:text-cyan-400 transition-colors"
              >
                u/{post.author.username}
              </Link>
              {post.author.karma !== undefined && post.author.karma > 0 && (
                <KarmaBadge karma={post.author.karma} size="xs" />
              )}
            </span>
            <span className="text-[10px] text-neutral-700">-</span>
            <span className="text-[10px] text-neutral-600">{formatDistanceToNow(post.createdAt)}</span>
          </div>

          {/* Title */}
          <Link href={`/forum/post/${post.id}`}>
            <h3 className="text-base font-bold text-white group-hover:text-cyan-400 transition-colors leading-snug mb-2">
              {post.title}
            </h3>
          </Link>

          {/* Preview text */}
          {post.contentPreview && (
            <p className="text-neutral-500 text-sm line-clamp-2 mb-4 leading-relaxed">
              {post.contentPreview}
            </p>
          )}

          {/* Action bar */}
          <div className="flex items-center gap-1">
            <Link
              href={`/forum/post/${post.id}`}
              className="flex items-center gap-2 px-3 py-1.5 rounded-lg text-[10px] font-black uppercase tracking-wider text-neutral-500 hover:bg-white/5 hover:text-cyan-400 transition-all"
            >
              <MessageSquare size={14} />
              <span>{post.commentCount} Comments</span>
            </Link>
            <button
              onClick={handleShare}
              className={`flex items-center gap-2 px-3 py-1.5 rounded-lg text-[10px] font-black uppercase tracking-wider transition-all ${
                copied
                  ? 'text-emerald-400 bg-emerald-500/10'
                  : 'text-neutral-500 hover:bg-white/5 hover:text-white'
              }`}
            >
              {copied ? <Check size={14} /> : <Share2 size={14} />}
              <span className="hidden sm:inline">{copied ? 'Copied!' : 'Share'}</span>
            </button>
            <button
              onClick={handleSave}
              disabled={isBookmarking}
              className={`flex items-center gap-2 px-3 py-1.5 rounded-lg text-[10px] font-black uppercase tracking-wider transition-all disabled:opacity-50 ${
                isSaved
                  ? 'text-cyan-400 bg-cyan-500/10'
                  : 'text-neutral-500 hover:bg-white/5 hover:text-white'
              }`}
            >
              {isBookmarking ? (
                <Loader2 size={14} className="animate-spin" />
              ) : isSaved ? (
                <BookmarkCheck size={14} />
              ) : (
                <Bookmark size={14} />
              )}
              <span className="hidden sm:inline">{isSaved ? 'Saved' : 'Save'}</span>
            </button>
            <div className="relative">
              <button
                onClick={() => setShowMenu(!showMenu)}
                className="flex items-center gap-2 p-1.5 rounded-lg text-neutral-500 hover:bg-white/5 hover:text-white transition-all"
              >
                <MoreHorizontal size={14} />
              </button>
              {showMenu && (
                <>
                  <div
                    className="fixed inset-0 z-40"
                    onClick={() => setShowMenu(false)}
                  />
                  <div className="absolute right-0 top-full mt-1 z-50 min-w-[140px] rounded-xl bg-neutral-900 border border-white/10 shadow-xl overflow-hidden">
                    <button
                      onClick={handleReport}
                      className="w-full flex items-center gap-2 px-4 py-2.5 text-[10px] font-black uppercase tracking-wider text-neutral-400 hover:bg-white/5 hover:text-red-400 transition-all"
                    >
                      <Flag size={12} />
                      Report
                    </button>
                  </div>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </article>
  );
}

