'use client';

import { useState, useEffect } from 'react';
import Link from 'next/link';
import { Award, Trophy, Loader2 } from 'lucide-react';
import { api } from '@/lib/api';
import { KarmaLeaderboardEntry, getKarmaTier, getKarmaTierColor } from '@/types/forum';
import { KarmaBadge } from './KarmaBadge';

interface KarmaLeaderboardProps {
  limit?: number;
  showTitle?: boolean;
}

/**
 * Karma leaderboard widget showing top contributors by karma
 */
export function KarmaLeaderboard({ limit = 5, showTitle = true }: KarmaLeaderboardProps) {
  const [entries, setEntries] = useState<KarmaLeaderboardEntry[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        setIsLoading(true);
        const response = await api.getKarmaLeaderboard({ limit });
        setEntries(response.entries);
      } catch (err) {
        console.error('Failed to load karma leaderboard:', err);
        setError('Failed to load leaderboard');
      } finally {
        setIsLoading(false);
      }
    };

    fetchLeaderboard();
  }, [limit]);

  const getRankStyle = (rank: number) => {
    if (rank === 1) return 'text-amber-400 bg-amber-500/10 border-amber-500/30';
    if (rank === 2) return 'text-neutral-300 bg-neutral-400/10 border-neutral-400/30';
    if (rank === 3) return 'text-amber-600 bg-amber-700/10 border-amber-700/30';
    return 'text-neutral-600 bg-neutral-800/50 border-neutral-700/30';
  };

  if (error) {
    return null;
  }

  return (
    <div className="p-6 rounded-2xl bg-neutral-900/30 border border-white/5">
      {showTitle && (
        <div className="flex items-center gap-2 mb-6">
          <Trophy size={14} className="text-amber-400" />
          <h3 className="text-[10px] font-black uppercase tracking-[0.2em] text-neutral-500">
            Top Contributors
          </h3>
        </div>
      )}

      {isLoading ? (
        <div className="flex items-center justify-center py-8">
          <Loader2 size={20} className="text-neutral-600 animate-spin" />
        </div>
      ) : entries.length === 0 ? (
        <p className="text-neutral-600 text-xs text-center py-8">
          No contributors yet
        </p>
      ) : (
        <div className="space-y-3">
          {entries.map((entry) => {
            const tier = getKarmaTier(entry.forumKarma);
            const tierColor = getKarmaTierColor(tier);

            return (
              <Link
                key={entry.userId}
                href={`/forum/user/${entry.userId}`}
                className="flex items-center gap-3 group"
              >
                {/* Rank */}
                <span
                  className={`w-6 h-6 rounded-md border flex items-center justify-center text-[10px] font-black ${getRankStyle(entry.rank)}`}
                >
                  {entry.rank}
                </span>

                {/* User info */}
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2">
                    <span className="text-xs text-neutral-400 group-hover:text-cyan-400 transition-colors truncate">
                      u/{entry.username}
                    </span>
                    <KarmaBadge karma={entry.forumKarma} size="xs" />
                  </div>
