// === NOTIFICATION RESPONSE TYPES ===

interface NotificationActorResponse {
  id: string;
  username: string;
}

interface NotificationResponse {
  id: string;
  type: 'reply' | 'mention' | 'vote' | 'system' | 'milestone';
  title: string;
  content: string | null;
  sourceType: 'post' | 'comment' | 'system' | null;
  sourceId: string | null;
  postId: string | null;
  actor: NotificationActorResponse | null;
  link: string | null;
  read: boolean;
  createdAt: string;
  readAt: string | null;
}

interface NotificationListResponse {
  notifications: NotificationResponse[];
  totalCount: number;
  unreadCount: number;
