  VoteResponse,
  ReportCreate,
  Report,
  GuideSection,
  RecommendedForum,
  SortOrder,
  CategoryListItem,
  PostAuthor,
  ForumSearchResult,
  ForumSearchResponse,
  SearchResultType,
  ForumUserProfile,
  UserPostItem,
  UserPostListResponse,
  UserCommentItem,
  UserCommentListResponse,
  BookmarkToggleResponse,
  BookmarkStatus,
  BookmarkedPostsResponse,
  // Admin/Moderation types
  ReportDetail,
  AdminReportListResponse,
  ResolveReportRequest,
  ModerationStats,
  BanUserRequest,
