// === NOTIFICATION TRANSFORM FUNCTIONS ===

function transformNotification(data: NotificationApiResponse): NotificationResponse {
  return {
    id: data.id,
    type: data.type,
    title: data.title,
    content: data.content,
    sourceType: data.source_type,
    sourceId: data.source_id,
    postId: data.post_id,
    actor: data.actor ? { id: data.actor.id, username: data.actor.username } : null,
    link: data.link,
    read: data.read,
    createdAt: data.created_at,
    readAt: data.read_at,
  };
}

function transformNotificationListResponse(data: NotificationListApiResponse): NotificationListResponse {
  return {
    notifications: data.notifications.map(transformNotification),
    totalCount: data.total_count,
    unreadCount: data.unread_count,
    hasMore: data.has_more,
  };
}

interface ApiOptions {
  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
  body?: unknown;
  token?: string;
}

interface Analysis {
  id: string;
  front_image_url: string | null;
  side_image_url: string | null;
  front_landmarks: Record<string, unknown> | null;
  side_landmarks: Record<string, unknown> | null;
  scores: Record<string, unknown> | null;
  gender: string | null;
  ethnicity: string | null;
  created_at: string;
}

class ApiClient {
  private token: string | null = null;

  setToken(token: string | null) {
    this.token = token;
  }

  getToken(): string | null {
    if (typeof window !== 'undefined') {
      return this.token || localStorage.getItem('auth_token');
    }
    return this.token;
  }

  private async request<T>(endpoint: string, options: ApiOptions = {}): Promise<T> {
    const { method = 'GET', body, token } = options;
    const authToken = token || this.getToken();

    const headers: Record<string, string> = {
      'Content-Type': 'application/json',
    };

    if (authToken) {
      headers['Authorization'] = `Bearer ${authToken}`;
    }

    try {
      const response = await fetch(`${API_URL}${endpoint}`, {
        method,
        headers,
        body: body ? JSON.stringify(body) : undefined,
      });

      if (!response.ok) {
        const error = await response.json().catch(() => ({ detail: 'Request failed' }));
        throw new Error(error.detail || `HTTP ${response.status}`);
      }

      // Handle empty responses (204 No Content)
      if (response.status === 204) {
        return undefined as T;
      }

      return response.json();
    } catch (err) {
      // Network errors, CORS issues, etc.
      if (err instanceof TypeError && err.message.includes('fetch')) {
        throw new Error('Network error - please check your internet connection');
      }
      throw err;
    }
  }

