/**
 * API Client for LOOKSMAXX Backend
 *
 * Handles all authenticated requests to the Railway-hosted API
 */

import type { UserRank, LeaderboardEntry, LeaderboardData, UserProfile } from '@/types/results';
import type {
  Category,
  SubForum,
  Post,
  PostListItem,
  PostListResponse,
  PostCreate,
  PostUpdate,
  Comment,
  CommentCreate,
  VoteType,
  VoteResponse,
  ReportCreate,
  Report,
  GuideSection,
  RecommendedForum,
  SortOrder,
  CategoryListItem,
  PostAuthor,
  ForumSearchResult,
  ForumSearchResponse,
  SearchResultType,
  ForumUserProfile,
  UserPostItem,
  UserPostListResponse,
  UserCommentItem,
  UserCommentListResponse,
  BookmarkToggleResponse,
  BookmarkStatus,
  BookmarkedPostsResponse,
  // Admin/Moderation types
  ReportDetail,
  AdminReportListResponse,
  ResolveReportRequest,
  ModerationStats,
  BanUserRequest,
  BanUserResponse,
  BannedUser,
  BannedUsersResponse,
  ReportStatusEnum,
  ActionTaken,
  TargetType,
  ReportReason,
  KarmaLeaderboardEntry,
  KarmaLeaderboardResponse,
  ForumQuota,
} from '@/types/forum';
import type {
  NotificationListResponse,
  MarkReadResponse,
  MarkAllReadResponse,
  DeleteNotificationResponse,
} from '@/types/notification';

const API_URL = (process.env.NEXT_PUBLIC_API_URL || 'http://localhost:8000').trim();

// API Response types (snake_case from backend)
interface LeaderboardApiResponse {
  user_id: string;
  score: number;
  global_rank: number;
  gender_rank: number;
  percentile: number;
  total_users: number;
  gender_total: number;
  anonymous_name: string;
  updated_at: string;
}

interface LeaderboardEntryResponse {
  user_id: string;
  rank: number;
  score: number;
  anonymous_name: string;
  gender: 'male' | 'female';
  face_photo_url: string | null;
  is_current_user: boolean;
  top_strengths: string[];
  top_improvements: string[];
}

interface LeaderboardListResponse {
  entries: LeaderboardEntryResponse[];
  total_count: number;
  user_rank: LeaderboardApiResponse | null;
}

interface UserProfileResponse {
  user_id: string;
  rank: number;
  score: number;
  anonymous_name: string;
  gender: 'male' | 'female';
  face_photo_url: string | null;
  top_strengths: string[];
  top_improvements: string[];
}

// Transform functions (snake_case -> camelCase)
function transformUserRank(data: LeaderboardApiResponse): UserRank {
  return {
    userId: data.user_id,
    score: data.score,
    globalRank: data.global_rank,
    genderRank: data.gender_rank,
    percentile: data.percentile,
    totalUsers: data.total_users,
    genderTotal: data.gender_total,
    anonymousName: data.anonymous_name,
    updatedAt: data.updated_at,
  };
}

function transformLeaderboardEntry(data: LeaderboardEntryResponse): LeaderboardEntry {
  return {
    userId: data.user_id,
    rank: data.rank,
    score: data.score,
    anonymousName: data.anonymous_name,
    gender: data.gender,
    facePhotoUrl: data.face_photo_url,
    isCurrentUser: data.is_current_user,
    topStrengths: data.top_strengths || [],
    topImprovements: data.top_improvements || [],
  };
}

function transformLeaderboardData(data: LeaderboardListResponse): LeaderboardData {
  return {
    entries: data.entries.map(transformLeaderboardEntry),
    totalCount: data.total_count,
    userRank: data.user_rank ? transformUserRank(data.user_rank) : null,
  };
}

function transformUserProfile(data: UserProfileResponse): UserProfile {
  return {
    userId: data.user_id,
    rank: data.rank,
    score: data.score,
    anonymousName: data.anonymous_name,
    gender: data.gender,
