'use client';

import { useEffect, useState, useCallback } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import {
  Bell,
  MessageSquare,
  ThumbsUp,
  AtSign,
  Info,
  Award,
  CheckCheck,
  Trash2,
  ArrowLeft,
  Loader2,
} from 'lucide-react';
import { api } from '@/lib/api';
import { Notification } from '@/types/notification';
import { ForumHeader, ForumBreadcrumb } from '@/components/forum/ForumHeader';

type NotificationType = 'reply' | 'mention' | 'vote' | 'system' | 'milestone';

export default function NotificationsPage() {
  const router = useRouter();
  const [notifications, setNotifications] = useState<NotificationResponse[]>([]);
  const [totalCount, setTotalCount] = useState(0);
  const [unreadCount, setUnreadCount] = useState(0);
  const [hasMore, setHasMore] = useState(false);
  const [loading, setLoading] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [filter, setFilter] = useState<'all' | 'unread'>('all');

  const fetchNotifications = useCallback(async (offset = 0, append = false) => {
    try {
      if (offset === 0) {
        setLoading(true);
      } else {
        setLoadingMore(true);
      }
      setError(null);

      const response = await api.getNotifications({
        unreadOnly: filter === 'unread',
        limit: 20,
        offset,
      });

      if (append) {
        setNotifications(prev => [...prev, ...response.notifications]);
      } else {
        setNotifications(response.notifications);
      }

      setTotalCount(response.totalCount);
      setUnreadCount(response.unreadCount);
      setHasMore(response.hasMore);
    } catch (err) {
      setError('Failed to load notifications');
      console.error('Failed to fetch notifications:', err);
    } finally {
      setLoading(false);
      setLoadingMore(false);
    }
  }, [filter]);

  useEffect(() => {
    const token = api.getToken();
    if (!token) {
      router.push('/login');
      return;
    }
    fetchNotifications();
  }, [fetchNotifications, router]);

  const handleLoadMore = () => {
    if (!loadingMore && hasMore) {
      fetchNotifications(notifications.length, true);
    }
  };

  const handleMarkAllRead = async () => {
    try {
      await api.markAllNotificationsRead();
      setNotifications(prev => prev.map(n => ({ ...n, read: true })));
      setUnreadCount(0);
    } catch (err) {
      console.error('Failed to mark all as read:', err);
    }
  };

  const handleMarkRead = async (notificationId: string) => {
    try {
      await api.markNotificationRead(notificationId);
      setNotifications(prev =>
        prev.map(n => (n.id === notificationId ? { ...n, read: true } : n))
      );
      setUnreadCount(prev => Math.max(0, prev - 1));
    } catch (err) {
      console.error('Failed to mark as read:', err);
    }
  };

  const handleDelete = async (notificationId: string, e: React.MouseEvent) => {
    e.stopPropagation();
    try {
      await api.deleteNotification(notificationId);
      setNotifications(prev => prev.filter(n => n.id !== notificationId));
      setTotalCount(prev => prev - 1);
    } catch (err) {
      console.error('Failed to delete notification:', err);
    }
  };

  const handleNotificationClick = async (notification: NotificationResponse) => {
    if (!notification.read) {
      await handleMarkRead(notification.id);
    }
    if (notification.link) {
      router.push(notification.link);
    }
  };

  const getNotificationIcon = (type: NotificationType) => {
    switch (type) {
      case 'reply':
        return <MessageSquare size={18} className="text-cyan-400" />;
      case 'vote':
        return <ThumbsUp size={18} className="text-green-400" />;
      case 'mention':
        return <AtSign size={18} className="text-purple-400" />;
      case 'milestone':
        return <Award size={18} className="text-yellow-400" />;
      case 'system':
      default:
        return <Info size={18} className="text-blue-400" />;
    }
  };

  const formatTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);

    if (diffMins < 1) return 'just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    if (diffDays < 7) return `${diffDays}d ago`;
    return date.toLocaleDateString();
  };

  return (
    <div className="min-h-screen bg-black">
      <ForumHeader />

      <ForumBreadcrumb
        items={[
          { label: 'Forum', href: '/forum' },
          { label: 'Notifications' },
        ]}
      />

      <div className="max-w-3xl mx-auto px-4 sm:px-6 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div>
            <h1 className="text-2xl font-bold text-white">Notifications</h1>
            <p className="text-sm text-neutral-500 mt-1">
              {unreadCount > 0
                ? `${unreadCount} unread notification${unreadCount !== 1 ? 's' : ''}`
                : 'All caught up!'}
            </p>
          </div>

          {unreadCount > 0 && (
            <button
              onClick={handleMarkAllRead}
              className="flex items-center gap-2 px-4 py-2 rounded-lg bg-cyan-500/10 border border-cyan-500/30 text-cyan-400 text-sm font-medium hover:bg-cyan-500/20 transition-colors"
            >
              <CheckCheck size={16} />
              Mark all read
            </button>
          )}
        </div>

        {/* Filter Tabs */}
        <div className="flex gap-2 mb-6">
          <button
            onClick={() => setFilter('all')}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
              filter === 'all'
                ? 'bg-white/10 text-white'
                : 'text-neutral-500 hover:text-white hover:bg-white/5'
            }`}
          >
            All
          </button>
          <button
            onClick={() => setFilter('unread')}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
              filter === 'unread'
                ? 'bg-white/10 text-white'
                : 'text-neutral-500 hover:text-white hover:bg-white/5'
            }`}
          >
            Unread
            {unreadCount > 0 && (
              <span className="ml-2 px-1.5 py-0.5 rounded bg-cyan-500 text-black text-[10px] font-bold">
                {unreadCount}
              </span>
            )}
          </button>
        </div>

        {/* Notifications List */}
        {loading ? (
          <div className="flex items-center justify-center py-16">
            <Loader2 size={32} className="animate-spin text-cyan-500" />
          </div>
        ) : error ? (
          <div className="text-center py-16">
            <p className="text-red-400 mb-4">{error}</p>
            <button
              onClick={() => fetchNotifications()}
              className="text-cyan-400 hover:text-cyan-300"
            >
              Try again
            </button>
          </div>
        ) : notifications.length === 0 ? (
          <div className="text-center py-16">
            <Bell size={48} className="mx-auto text-neutral-600 mb-4" />
            <p className="text-neutral-400 text-lg mb-2">No notifications</p>
            <p className="text-neutral-600 text-sm">
              {filter === 'unread'
                ? "You're all caught up!"
                : "You don't have any notifications yet."}
            </p>
          </div>
        ) : (
          <div className="space-y-2">
            {notifications.map((notification) => (
              <div
                key={notification.id}
                onClick={() => handleNotificationClick(notification)}
                className={`group flex items-start gap-4 p-4 rounded-xl border transition-all cursor-pointer ${
                  notification.read
                    ? 'bg-neutral-900/50 border-white/5 hover:border-white/10'
                    : 'bg-cyan-500/5 border-cyan-500/20 hover:border-cyan-500/40'
                }`}
              >
                {/* Icon */}
                <div className="flex-shrink-0 w-10 h-10 rounded-lg bg-white/5 flex items-center justify-center">
                  {getNotificationIcon(notification.type as NotificationType)}
                </div>

                {/* Content */}
                <div className="flex-1 min-w-0">
                  <p
                    className={`text-sm ${
                      notification.read ? 'text-neutral-300' : 'text-white font-medium'
                    }`}
                  >
                    {notification.title}
                  </p>
                  {notification.content && (
                    <p className="text-sm text-neutral-500 mt-1 line-clamp-2">
                      {notification.content}
                    </p>
                  )}
                  <p className="text-xs text-neutral-600 mt-2">
                    {formatTimeAgo(notification.createdAt)}
                  </p>
                </div>

                {/* Actions */}
                <div className="flex-shrink-0 flex items-center gap-2">
                  {!notification.read && (
                    <div className="w-2 h-2 rounded-full bg-cyan-400" />
                  )}
                  <button
                    onClick={(e) => handleDelete(notification.id, e)}
                    className="p-2 rounded-lg text-neutral-600 hover:text-red-400 hover:bg-white/5 opacity-0 group-hover:opacity-100 transition-all"
                    title="Delete notification"
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
            ))}

            {/* Load More */}
            {hasMore && (
              <div className="pt-4 text-center">
                <button
                  onClick={handleLoadMore}
                  disabled={loadingMore}
                  className="px-6 py-2 rounded-lg bg-white/5 border border-white/10 text-sm text-white hover:bg-white/10 transition-colors disabled:opacity-50"
                >
                  {loadingMore ? (
                    <Loader2 size={16} className="animate-spin mx-auto" />
                  ) : (
                    'Load more'
                  )}
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

