     1→/**
     2→ * Side Profile Detection using Image Processing
     3→ *
     4→ * Since MediaPipe doesn't work well with side profiles, this module uses
     5→ * edge detection and contour analysis to find the profile silhouette
     6→ * and estimate landmark positions.
     7→ */
     8→
     9→import { SIDE_PROFILE_LANDMARKS } from './landmarks';
    10→
    11→interface Point {
    12→  x: number;
    13→  y: number;
    14→}
    15→
    16→interface ProfileContour {
    17→  points: Point[];
    18→  boundingBox: { x: number; y: number; width: number; height: number };
    19→  orientation: 'left' | 'right'; // Which direction the face is pointing
    20→}
    21→
    22→interface SideProfileResult {
    23→  landmarks: Array<{ id: string; x: number; y: number }>;
    24→  confidence: number;
    25→  contour: Point[];
    26→  orientation: 'left' | 'right';
    27→}
    28→
    29→/**
    30→ * Detect side profile landmarks from an image
    31→ */
    32→export async function detectSideProfile(
    33→  imageUrl: string
    34→): Promise<SideProfileResult | null> {
    35→  try {
    36→    const img = await loadImage(imageUrl);
    37→    const canvas = document.createElement('canvas');
    38→    const ctx = canvas.getContext('2d');
    39→    if (!ctx) return null;
    40→
    41→    canvas.width = img.width;
    42→    canvas.height = img.height;
    43→    ctx.drawImage(img, 0, 0);
    44→
    45→    // Get image data
    46→    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    47→
    48→    // Step 1: Convert to grayscale
    49→    const grayscale = toGrayscale(imageData);
    50→
    51→    // Step 2: Apply Gaussian blur to reduce noise
    52→    const blurred = gaussianBlur(grayscale, canvas.width, canvas.height);
    53→
    54→    // Step 3: Edge detection (Sobel operator)
    55→    const edges = sobelEdgeDetection(blurred, canvas.width, canvas.height);
    56→
    57→    // Step 4: Find the profile contour
    58→    const contour = extractProfileContour(edges, canvas.width, canvas.height);
    59→
    60→    if (!contour || contour.points.length < 10) {
    61→      console.log('[SideProfile] Could not extract sufficient contour points');
    62→      return null;
    63→    }
    64→
    65→    // Step 5: Analyze contour to find key anchor points
    66→    const anchors = findAnchorPoints(contour);
    67→
    68→    // Step 6: Map anchors to our 28 landmarks
    69→    const landmarks = mapToLandmarks(anchors, contour);
    70→
    71→    return {
    72→      landmarks,
    73→      confidence: 0.7, // Lower confidence than MediaPipe frontal
    74→      contour: contour.points,
    75→      orientation: contour.orientation,
    76→    };
    77→  } catch (error) {
    78→    console.error('[SideProfile] Detection error:', error);
    79→    return null;
    80→  }
    81→}
    82→
    83→/**
    84→ * Load an image from URL
    85→ */
    86→function loadImage(url: string): Promise<HTMLImageElement> {
    87→  return new Promise((resolve, reject) => {
    88→    const img = new Image();
    89→    img.crossOrigin = 'anonymous';
    90→    img.onload = () => resolve(img);
    91→    img.onerror = reject;
    92→    img.src = url;
    93→  });
    94→}
    95→
    96→/**
    97→ * Convert image data to grayscale array
    98→ */
    99→function toGrayscale(imageData: ImageData): Uint8Array {
   100→  const data = imageData.data;
   101→  const gray = new Uint8Array(data.length / 4);
   102→
   103→  for (let i = 0; i < gray.length; i++) {
   104→    const idx = i * 4;
   105→    // Luminosity method for grayscale
   106→    gray[i] = Math.round(0.299 * data[idx] + 0.587 * data[idx + 1] + 0.114 * data[idx + 2]);
   107→  }
   108→
   109→  return gray;
   110→}
   111→
   112→/**
   113→ * Apply 3x3 Gaussian blur
   114→ */
   115→function gaussianBlur(gray: Uint8Array, width: number, height: number): Uint8Array {
   116→  const kernel = [
   117→    1/16, 2/16, 1/16,
   118→    2/16, 4/16, 2/16,
   119→    1/16, 2/16, 1/16
   120→  ];
   121→
   122→  const result = new Uint8Array(gray.length);
   123→
   124→  for (let y = 1; y < height - 1; y++) {
   125→    for (let x = 1; x < width - 1; x++) {
   126→      let sum = 0;
   127→      let k = 0;
   128→      for (let ky = -1; ky <= 1; ky++) {
   129→        for (let kx = -1; kx <= 1; kx++) {
   130→          sum += gray[(y + ky) * width + (x + kx)] * kernel[k++];
   131→        }
   132→      }
   133→      result[y * width + x] = sum;
   134→    }
   135→  }
   136→
   137→  return result;
   138→}
   139→
   140→/**
   141→ * Sobel edge detection
   142→ */
   143→function sobelEdgeDetection(gray: Uint8Array, width: number, height: number): Uint8Array {
   144→  const sobelX = [-1, 0, 1, -2, 0, 2, -1, 0, 1];
   145→  const sobelY = [-1, -2, -1, 0, 0, 0, 1, 2, 1];
   146→
   147→  const edges = new Uint8Array(gray.length);
   148→
   149→  for (let y = 1; y < height - 1; y++) {
   150→    for (let x = 1; x < width - 1; x++) {
   151→      let gx = 0, gy = 0;
   152→      let k = 0;
   153→
   154→      for (let ky = -1; ky <= 1; ky++) {
   155→        for (let kx = -1; kx <= 1; kx++) {
   156→          const pixel = gray[(y + ky) * width + (x + kx)];
   157→          gx += pixel * sobelX[k];
   158→          gy += pixel * sobelY[k];
   159→          k++;
   160→        }
   161→      }
   162→
   163→      // Magnitude
   164→      edges[y * width + x] = Math.min(255, Math.sqrt(gx * gx + gy * gy));
   165→    }
   166→  }
   167→
   168→  return edges;
   169→}
   170→
   171→/**
   172→ * Extract the profile contour from edge image
   173→ * Looks for the strongest vertical edge on each side
   174→ */
   175→function extractProfileContour(
   176→  edges: Uint8Array,
   177→  width: number,
   178→  height: number
   179→): ProfileContour | null {
   180→  // Determine if face is on left or right side of image
   181→  let leftEdgeSum = 0;
   182→  let rightEdgeSum = 0;
   183→
   184→  const midX = width / 2;
   185→
   186→  for (let y = 0; y < height; y++) {
   187→    for (let x = 0; x < width; x++) {
   188→      const val = edges[y * width + x];
   189→      if (x < midX) {
   190→        leftEdgeSum += val;
   191→      } else {
   192→        rightEdgeSum += val;
   193→      }
   194→    }
   195→  }
   196→
   197→  // Face is likely on the side with more edge activity
   198→  const faceOnLeft = leftEdgeSum > rightEdgeSum;
   199→  const orientation: 'left' | 'right' = faceOnLeft ? 'right' : 'left'; // pointing direction
   200→
   201→  // Find the profile edge - scan from the face side inward
   202→  const contourPoints: Point[] = [];
   203→  const threshold = 30; // Edge threshold
   204→
   205→  // Scan each row to find the profile edge
   206→  for (let y = Math.floor(height * 0.1); y < Math.floor(height * 0.95); y++) {
   207→    let edgeX = -1;
   208→    let maxEdge = threshold;
   209→
   210→    if (faceOnLeft) {
   211→      // Scan from left, find rightmost strong edge in left half
   212→      for (let x = 0; x < midX; x++) {
   213→        if (edges[y * width + x] > maxEdge) {
   214→          maxEdge = edges[y * width + x];
   215→          edgeX = x;
   216→        }
   217→      }
   218→    } else {
   219→      // Scan from right, find leftmost strong edge in right half
   220→      for (let x = width - 1; x >= midX; x--) {
   221→        if (edges[y * width + x] > maxEdge) {
   222→          maxEdge = edges[y * width + x];
   223→          edgeX = x;
   224→        }
   225→      }
   226→    }
   227→
   228→    if (edgeX !== -1) {
   229→      contourPoints.push({ x: edgeX / width, y: y / height });
   230→    }
   231→  }
   232→
   233→  if (contourPoints.length < 10) return null;
   234→
   235→  // Smooth the contour using moving average
   236→  const smoothedPoints = smoothContour(contourPoints, 5);
   237→
   238→  // Calculate bounding box
   239→  let minX = 1, minY = 1, maxX = 0, maxY = 0;
   240→  smoothedPoints.forEach(p => {
   241→    minX = Math.min(minX, p.x);
   242→    minY = Math.min(minY, p.y);
   243→    maxX = Math.max(maxX, p.x);
   244→    maxY = Math.max(maxY, p.y);
   245→  });
   246→
   247→  return {
   248→    points: smoothedPoints,
   249→    boundingBox: { x: minX, y: minY, width: maxX - minX, height: maxY - minY },
   250→    orientation,
   251→  };
   252→}
   253→
   254→/**
   255→ * Smooth contour points using moving average
   256→ */
   257→function smoothContour(points: Point[], windowSize: number): Point[] {
   258→  const smoothed: Point[] = [];
   259→  const half = Math.floor(windowSize / 2);
   260→
   261→  for (let i = 0; i < points.length; i++) {
   262→    let sumX = 0, sumY = 0, count = 0;
   263→
   264→    for (let j = -half; j <= half; j++) {
   265→      const idx = i + j;
   266→      if (idx >= 0 && idx < points.length) {
   267→        sumX += points[idx].x;
   268→        sumY += points[idx].y;
   269→        count++;
   270→      }
   271→    }
   272→
   273→    smoothed.push({ x: sumX / count, y: sumY / count });
   274→  }
   275→
   276→  return smoothed;
   277→}
   278→
   279→interface AnchorPoints {
   280→  vertex: Point;      // Top of head
   281→  forehead: Point;    // Forehead profile
   282→  glabella: Point;    // Between brows
   283→  nasion: Point;      // Nasal root
   284→  noseTip: Point;     // Pronasale
   285→  subnasale: Point;   // Base of nose
   286→  upperLip: Point;    // Labrale superius
   287→  lowerLip: Point;    // Labrale inferius
   288→  chin: Point;        // Pogonion
   289→  menton: Point;      // Bottom of chin
   290→  neckPoint: Point;   // Neck
   291→  jawAngle: Point;    // Gonion
   292→}
   293→
   294→/**
   295→ * Find anchor points along the profile contour
   296→ */
   297→function findAnchorPoints(
   298→  contour: ProfileContour
   299→): AnchorPoints {
   300→  const points = contour.points;
   301→
   302→  // Helper to find point at approximate Y position
   303→  const findPointAtY = (targetY: number): Point => {
   304→    let closest = points[0];
   305→    let minDist = Math.abs(points[0].y - targetY);
   306→
   307→    for (const p of points) {
   308→      const dist = Math.abs(p.y - targetY);
   309→      if (dist < minDist) {
   310→        minDist = dist;
   311→        closest = p;
   312→      }
   313→    }
   314→    return closest;
   315→  };
   316→
   317→  // Helper to find the most protruding point in a Y range
   318→  const findProtrudingPoint = (yStart: number, yEnd: number, direction: 'left' | 'right'): Point => {
   319→    let best = points[0];
   320→
   321→    for (const p of points) {
   322→      if (p.y >= yStart && p.y <= yEnd) {
   323→        if (direction === 'left' && p.x < best.x) best = p;
   324→        if (direction === 'right' && p.x > best.x) best = p;
   325→      }
   326→    }
   327→    return best;
   328→  };
   329→
   330→  // Find key inflection points based on profile geometry
   331→  const isLeftFacing = contour.orientation === 'left';
   332→  const protrusion = isLeftFacing ? 'left' : 'right';
   333→
   334→  // Divide face into regions based on typical proportions
   335→  const vertex = findPointAtY(0.05);
   336→  const forehead = findPointAtY(0.15);
   337→  const glabella = findPointAtY(0.28);
   338→  const nasion = findPointAtY(0.32);
   339→
   340→  // Nose tip is usually the most protruding point between y=0.35 and y=0.55
   341→  const noseTip = findProtrudingPoint(0.35, 0.55, protrusion);
   342→
   343→  const subnasale = findPointAtY(0.58);
   344→  const upperLip = findPointAtY(0.62);
   345→  const lowerLip = findPointAtY(0.68);
   346→
   347→  // Chin (pogonion) is often protruding
   348→  const chin = findProtrudingPoint(0.75, 0.85, protrusion);
   349→
   350→  const menton = findPointAtY(0.88);
   351→  const neckPoint = findPointAtY(0.95);
   352→
   353→  // Jaw angle - find where contour curves from vertical to horizontal
   354→  // This is typically around y=0.7-0.8, and set back from the chin
   355→  const jawAngle = findPointAtY(0.75);
   356→
   357→  return {
   358→    vertex,
   359→    forehead,
   360→    glabella,
   361→    nasion,
   362→    noseTip,
   363→    subnasale,
   364→    upperLip,
   365→    lowerLip,
   366→    chin,
   367→    menton,
   368→    neckPoint,
   369→    jawAngle,
   370→  };
   371→}
   372→
   373→/**
   374→ * Map anchor points to our 28 side profile landmarks
   375→ */
   376→function mapToLandmarks(
   377→  anchors: AnchorPoints,
   378→  contour: ProfileContour
   379→): Array<{ id: string; x: number; y: number }> {
   380→  const isLeft = contour.orientation === 'left';
   381→
   382→  // Helper to interpolate between two points
   383→  const lerp = (p1: Point, p2: Point, t: number): Point => ({
   384→    x: p1.x + (p2.x - p1.x) * t,
   385→    y: p1.y + (p2.y - p1.y) * t,
   386→  });
   387→
   388→  // Helper to offset a point horizontally (for ear/back of head estimation)
   389→  const offsetX = (p: Point, offset: number): Point => ({
   390→    x: p.x + (isLeft ? offset : -offset),
   391→    y: p.y,
   392→  });
   393→
   394→  // Map each of our 28 landmarks
   395→  // FaceIQ order: vertex, occiput, pronasale, neckPoint, porion, orbitale,
   396→  // tragus, intertragicNotch, cornealApex, cheekbone, trichion, glabella, nasion,
   397→  // rhinion, supratip, infratip, columella, subnasale, subalare, labraleSuperius,
   398→  // cheilion, labraleInferius, sublabiale, pogonion, menton, cervicalPoint, gonionTop, gonionBottom
   399→
   400→  const landmarkPositions: Record<string, Point> = {
   401→    // 1. vertex - top of head
   402→    vertex: anchors.vertex,
   403→
   404→    // 2. occiput - back of head (estimate based on vertex, offset back)
   405→    occiput: offsetX(anchors.vertex, 0.15),
   406→
   407→    // 3. pronasale - nose tip
   408→    pronasale: anchors.noseTip,
   409→
   410→    // 4. neckPoint - lower neck
   411→    neckPoint: anchors.neckPoint,
   412→
   413→    // 5. porion - ear canal (estimate based on jaw angle, offset back)
   414→    porion: offsetX({ x: anchors.jawAngle.x, y: anchors.nasion.y }, 0.08),
   415→
   416→    // 6. orbitale - lowest orbital rim (between glabella and nasion level)
   417→    orbitale: { x: lerp(anchors.glabella, anchors.nasion, 0.5).x, y: anchors.nasion.y - 0.02 },
   418→
   419→    // 7. tragus - ear cartilage
   420→    tragus: offsetX({ x: anchors.jawAngle.x, y: lerp(anchors.nasion, anchors.jawAngle, 0.3).y }, 0.06),
   421→
   422→    // 8. intertragicNotch - notch in ear
   423→    intertragicNotch: offsetX({ x: anchors.jawAngle.x, y: lerp(anchors.nasion, anchors.jawAngle, 0.5).y }, 0.07),
   424→
   425→    // 9. cornealApex - forward point of cornea
   426→    cornealApex: { x: anchors.glabella.x + (isLeft ? -0.02 : 0.02), y: anchors.glabella.y + 0.02 },
   427→
   428→    // 10. cheekbone - zygomatic prominence
   429→    cheekbone: lerp(anchors.nasion, anchors.jawAngle, 0.4),
   430→
   431→    // 11. trichion - hairline
   432→    trichion: anchors.forehead,
   433→
   434→    // 12. glabella - between brows
   435→    glabella: anchors.glabella,
   436→
   437→    // 13. nasion - nasal root
   438→    nasion: anchors.nasion,
   439→
   440→    // 14. rhinion - mid-dorsum of nose
   441→    rhinion: lerp(anchors.nasion, anchors.noseTip, 0.4),
   442→
   443→    // 15. supratip - above nose tip
   444→    supratip: lerp(anchors.nasion, anchors.noseTip, 0.75),
   445→
   446→    // 16. infratip - below nose tip
   447→    infratip: lerp(anchors.noseTip, anchors.subnasale, 0.3),
   448→
   449→    // 17. columella - nasal septum
   450→    columella: lerp(anchors.noseTip, anchors.subnasale, 0.5),
   451→
   452→    // 18. subnasale - base of nose
   453→    subnasale: anchors.subnasale,
   454→
   455→    // 19. subalare - nostril wing
   456→    subalare: { x: anchors.subnasale.x + (isLeft ? -0.02 : 0.02), y: anchors.subnasale.y - 0.01 },
   457→
   458→    // 20. labraleSuperius - upper lip
   459→    labraleSuperius: anchors.upperLip,
   460→
   461→    // 21. cheilion - mouth corner
   462→    cheilion: { x: lerp(anchors.upperLip, anchors.jawAngle, 0.3).x, y: lerp(anchors.upperLip, anchors.lowerLip, 0.5).y },
