    affiliateTag: 'looksmaxx-22',
    currency: 'USD',
    currencySymbol: '$',
  },
};

// ============================================
// PRODUCT TYPES
// ============================================

export type ProductCategory =
  | 'hygiene'
  | 'grooming'
  | 'skincare'
  | 'miscellaneous'
  | 'supplements'
  | 'hair'
  | 'beard'
  | 'teeth'
