     1â†’/**
     2â†’ * Region Detection & Link Resolution Utility
     3â†’ * Handles auto-detection and multi-region affiliate links
     4â†’ */
     5â†’
     6â†’import {
     7â†’  Region,
     8â†’  RegionConfig,
     9â†’  REGION_CONFIGS,
    10â†’  LOCALE_TO_REGION,
    11â†’  TIMEZONE_TO_REGION,
    12â†’  ProductRegionLinks,
    13â†’} from '@/types/guides';
    14â†’
    15â†’const STORAGE_KEY = 'looksmaxx_user_region';
    16â†’const DEFAULT_REGION: Region = 'us';
    17â†’
    18â†’/**
    19â†’ * Detection Priority:
    20â†’ * 1. localStorage (user preference - highest priority)
    21â†’ * 2. navigator.language (browser locale)
    22â†’ * 3. Intl.DateTimeFormat timezone (fallback)
    23â†’ * 4. 'us' (default fallback)
    24â†’ */
    25â†’export function detectRegion(): Region {
    26â†’  if (typeof window === 'undefined') {
    27â†’    return DEFAULT_REGION;
    28â†’  }
    29â†’
    30â†’  // 1. Check localStorage for user preference
    31â†’  try {
    32â†’    const stored = localStorage.getItem(STORAGE_KEY);
    33â†’    if (stored && isValidRegion(stored)) {
    34â†’      return stored as Region;
    35â†’    }
    36â†’  } catch {
    37â†’    // localStorage not available
    38â†’  }
    39â†’
    40â†’  // 2. Check browser locale
    41â†’  const locale = navigator.language;
    42â†’  if (locale && LOCALE_TO_REGION[locale]) {
    43â†’    return LOCALE_TO_REGION[locale];
    44â†’  }
    45â†’
    46â†’  // Try partial match (e.g., 'en' -> 'us')
    47â†’  const langCode = locale?.split('-')[0];
    48â†’  if (langCode) {
    49â†’    const partialMatch = Object.keys(LOCALE_TO_REGION).find(
    50â†’      (key) => key.startsWith(langCode + '-')
    51â†’    );
    52â†’    if (partialMatch) {
    53â†’      return LOCALE_TO_REGION[partialMatch];
    54â†’    }
    55â†’  }
    56â†’
    57â†’  // 3. Check timezone
    58â†’  try {
    59â†’    const timezone = Intl.DateTimeFormat().resolvedOptions().timeZone;
    60â†’    if (timezone && TIMEZONE_TO_REGION[timezone]) {
    61â†’      return TIMEZONE_TO_REGION[timezone];
    62â†’    }
    63â†’
    64â†’    // Try continent-based fallback
    65â†’    if (timezone) {
    66â†’      if (timezone.startsWith('America/')) return 'us';
    67â†’      if (timezone.startsWith('Europe/')) {
    68â†’        if (timezone.includes('London')) return 'uk';
    69â†’        if (timezone.includes('Paris')) return 'fr';
    70â†’        if (timezone.includes('Berlin')) return 'de';
    71â†’        return 'uk'; // Default European
    72â†’      }
    73â†’      if (timezone.startsWith('Australia/')) return 'au';
    74â†’      if (timezone.startsWith('Asia/')) return 'asia';
    75â†’    }
    76â†’  } catch {
    77â†’    // Intl not available
    78â†’  }
    79â†’
    80â†’  // 4. Default fallback
    81â†’  return DEFAULT_REGION;
    82â†’}
    83â†’
    84â†’/**
    85â†’ * Check if a region code is valid
    86â†’ */
    87â†’export function isValidRegion(region: string): region is Region {
    88â†’  return region in REGION_CONFIGS;
    89â†’}
    90â†’
    91â†’/**
    92â†’ * Get region configuration
    93â†’ */
    94â†’export function getRegionConfig(region: Region): RegionConfig {
    95â†’  return REGION_CONFIGS[region];
    96â†’}
    97â†’
    98â†’/**
    99â†’ * Save user's region preference to localStorage
   100â†’ */
   101â†’export function saveRegionPreference(region: Region): void {
   102â†’  if (typeof window === 'undefined') return;
   103â†’
   104â†’  try {
   105â†’    localStorage.setItem(STORAGE_KEY, region);
   106â†’  } catch {
   107â†’    // localStorage not available
   108â†’  }
   109â†’}
   110â†’
   111â†’/**
   112â†’ * Clear saved region preference
   113â†’ */
   114â†’export function clearRegionPreference(): void {
   115â†’  if (typeof window === 'undefined') return;
   116â†’
   117â†’  try {
   118â†’    localStorage.removeItem(STORAGE_KEY);
   119â†’  } catch {
   120â†’    // localStorage not available
   121â†’  }
   122â†’}
   123â†’
   124â†’/**
   125â†’ * Check if region was auto-detected vs user-selected
   126â†’ */
   127â†’export function isRegionAutoDetected(): boolean {
   128â†’  if (typeof window === 'undefined') return true;
   129â†’
   130â†’  try {
   131â†’    return !localStorage.getItem(STORAGE_KEY);
   132â†’  } catch {
   133â†’    return true;
   134â†’  }
   135â†’}
   136â†’
   137â†’/**
   138â†’ * Get affiliate link for a product based on region
   139â†’ * Falls back to US or direct link if region unavailable
   140â†’ */
   141â†’export function getProductLink(
   142â†’  regionLinks: ProductRegionLinks,
   143â†’  region: Region,
   144â†’  directLink?: string
   145â†’): string {
   146â†’  // Try requested region first
   147â†’  const regionLink = regionLinks[region];
   148â†’  if (regionLink) return regionLink;
   149â†’
   150â†’  // Fallback to AU (widest availability)
   151â†’  if (regionLinks.au) return regionLinks.au;
   152â†’
   153â†’  // Fallback to US
   154â†’  if (regionLinks.us) return regionLinks.us;
   155â†’
   156â†’  // Use direct link if available
   157â†’  if (directLink) return directLink;
   158â†’
   159â†’  // Last resort: return first available link
