## 6. COMPLETE EXAMPLE: Production-Ready Component

Here's a combined, production-ready implementation:

```typescript
// components/ArticleLayout.tsx
import React, { useState, useEffect } from "react";
import { cn } from "@/lib/utils";

interface TocItem {
  id: string;
  level: number;
  title: string;
}

interface ArticleLayoutProps {
  children: React.ReactNode;
}

export function ArticleLayout({ children }: ArticleLayoutProps) {
  const [toc, setToc] = useState<TocItem[]>([]);
  const [activeId, setActiveId] = useState<string>();

  // Extract headings on mount
  useEffect(() => {
    const timer = setTimeout(() => {
      const container = document.getElementById("article-content");
      if (!container) return;

      const headings = Array.from(
        container.querySelectorAll("h2, h3, h4")
      ) as HTMLElement[];

      const items = headings.map((heading, index) => {
        if (!heading.id) {
          heading.id = `section-${index}`;
        }
        return {
          id: heading.id,
          level: parseInt(heading.tagName[1]),
          title: heading.textContent?.trim() || "",
        };
      });

      setToc(items);

      // Setup intersection observer
      const observer = new IntersectionObserver(
        (entries) => {
          const visible = entries
            .filter((e) => e.isIntersecting)
            .sort((a, b) => {
              const aTop = a.target.getBoundingClientRect().top;
              const bTop = b.target.getBoundingClientRect().top;
              return aTop - bTop;
            });

          if (visible.length > 0) {
            setActiveId(visible[0].target.id);
          }
        },
        { rootMargin: "-100px 0px -66%", threshold: 0 }
      );

      headings.forEach((h) => observer.observe(h));

      return () => {
        headings.forEach((h) => observer.unobserve(h));
        observer.disconnect();
      };
    }, 100);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="grid md:grid-cols-[1fr_286px] gap-8">
      {/* Main content */}
      <article
        id="article-content"
        className="prose prose-lg max-w-full dark:prose-invert"
      >
        {children}
      </article>

      {/* Desktop TOC Sidebar */}
      <TableOfContents items={toc} activeId={activeId} />
    </div>
  );
}

function TableOfContents({
  items,
  activeId,
}: {
  items: TocItem[];
  activeId?: string;
}) {
  return (
    <nav className="hidden md:block sticky top-20 max-h-[calc(100vh-5rem)] overflow-y-auto">
      <div className="text-xs font-semibold uppercase text-gray-500 mb-4">
        On this page
      </div>
      <ul className="space-y-2 text-sm">
        {items.map((item) => (
          <li key={item.id} style={{ paddingLeft: `${(item.level - 2) * 12}px` }}>
            <a
              href={`#${item.id}`}
              onClick={(e) => {
                e.preventDefault();
                const target = document.getElementById(item.id);
                if (target) {
                  target.scrollIntoView({ behavior: "smooth", block: "start" });
                }
              }}
              className={cn(
                "block py-1 px-2 rounded transition-colors hover:text-primary-600",
                activeId === item.id
                  ? "text-primary-600 font-semibold bg-primary-50"
                  : "text-gray-600 hover:bg-gray-100"
              )}
            >
              {item.title}
            </a>
          </li>
        ))}
      </ul>
    </nav>
  );
}
