'use client';

import React, { createContext, useContext, useState, ReactNode } from 'react';

interface FacialMetrics {
  symmetry: { score: number; details: Record<string, number | string>; analysis: string };
  proportions: { score: number; details: Record<string, number | string>; analysis: string };
  eyes: { score: number; details: Record<string, number | string>; analysis: string };
  nose: { score: number; details: Record<string, number | string>; analysis: string };
  jawChin: { score: number; details: Record<string, number | string>; analysis: string };
  lipsMouth: { score: number; details: Record<string, number | string>; analysis: string };
  skinQuality: { score: number; details: Record<string, number | string>; analysis: string };
}

interface Recommendation {
  category: string;
  priority: 'high' | 'medium' | 'low';
  title: string;
  description: string;
}

interface AnalysisData {
  id: string;
  imageUrl: string;
  gender: 'male' | 'female';
  ethnicity: string;
  overallScore: number;
  percentile: number;
  metrics: FacialMetrics;
  recommendations: Recommendation[];
  createdAt: Date;
}

interface AnalysisContextType {
  gender: 'male' | 'female' | null;
  setGender: (gender: 'male' | 'female' | null) => void;
  ethnicity: string | null;
  setEthnicity: (ethnicity: string | null) => void;
  uploadedImage: string | null;
  setUploadedImage: (image: string | null) => void;
  analysisResult: AnalysisData | null;
  setAnalysisResult: (result: AnalysisData | null) => void;
  isAnalyzing: boolean;
  setIsAnalyzing: (analyzing: boolean) => void;
  currentStep: number;
  setCurrentStep: (step: number) => void;
  resetAnalysis: () => void;
}

const AnalysisContext = createContext<AnalysisContextType | undefined>(undefined);

export function AnalysisProvider({ children }: { children: ReactNode }) {
  const [gender, setGender] = useState<'male' | 'female' | null>(null);
  const [ethnicity, setEthnicity] = useState<string | null>(null);
  const [uploadedImage, setUploadedImage] = useState<string | null>(null);
  const [analysisResult, setAnalysisResult] = useState<AnalysisData | null>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);

  const resetAnalysis = () => {
    setGender(null);
    setEthnicity(null);
    setUploadedImage(null);
    setAnalysisResult(null);
    setIsAnalyzing(false);
    setCurrentStep(1);
  };

  return (
    <AnalysisContext.Provider
      value={{
        gender,
        setGender,
        ethnicity,
        setEthnicity,
        uploadedImage,
        setUploadedImage,
        analysisResult,
        setAnalysisResult,
        isAnalyzing,
        setIsAnalyzing,
        currentStep,
        setCurrentStep,
        resetAnalysis,
      }}
    >
      {children}
    </AnalysisContext.Provider>
  );
}

export function useAnalysis() {
  const context = useContext(AnalysisContext);
  if (context === undefined) {
    throw new Error('useAnalysis must be used within an AnalysisProvider');
  }
  return context;
}
</result>

