'use client';

import { useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { motion, AnimatePresence } from 'framer-motion';
import { useAnalysis } from '@/contexts/AnalysisContext';
import Header from '@/components/Header';
import Footer from '@/components/Footer';
import ProgressSteps from '@/components/ProgressSteps';
import PageTransition from '@/components/PageTransition';
import ImageUploader from '@/components/ImageUploader';
import { ArrowLeft, ArrowRight, User, Users } from 'lucide-react';

const ethnicities = [
  'Caucasian',
  'East Asian',
  'South Asian',
  'African',
  'Hispanic/Latino',
  'Middle Eastern',
  'Southeast Asian',
  'Pacific Islander',
  'Mixed/Other',
];

export default function UploadPage() {
  const router = useRouter();
  const {
    gender,
    setGender,
    ethnicity,
    setEthnicity,
    uploadedImage,
    setUploadedImage,
    currentStep,
    setCurrentStep,
    setIsAnalyzing,
    setAnalysisResult,
  } = useAnalysis();

  // Reset to step 1 on mount if no data
  useEffect(() => {
    if (!gender && currentStep > 1) {
      setCurrentStep(1);
    }
  }, [gender, currentStep, setCurrentStep]);

  const canProceed = () => {
    switch (currentStep) {
      case 1:
        return gender !== null;
      case 2:
        return ethnicity !== null;
      case 3:
        return uploadedImage !== null;
      default:
        return false;
    }
  };

  const handleNext = () => {
    if (currentStep < 3 && canProceed()) {
      setCurrentStep(currentStep + 1);
    } else if (currentStep === 3 && canProceed()) {
      // Start analysis
      handleAnalysis();
    }
  };

  const handleBack = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleAnalysis = async () => {
    setIsAnalyzing(true);
    router.push('/analysis');

    // Simulate analysis delay
    await new Promise((resolve) => setTimeout(resolve, 3000));

    // Mock analysis result
    const mockResult = {
      id: 'analysis-' + Date.now(),
      imageUrl: uploadedImage!,
      gender: gender!,
      ethnicity: ethnicity!,
      overallScore: 7.8,
      percentile: 85,
      metrics: {
        symmetry: {
          score: 8.2,
          details: { leftRightDeviation: '2.3%', verticalAlignment: '98%' },
          analysis: 'Excellent facial symmetry with minimal deviation between left and right sides.',
        },
        proportions: {
          score: 7.5,
          details: { thirdsRatio: '1:1.1:0.95', goldenRatio: '1.58', fwhr: '1.85' },
          analysis: 'Good facial proportions with slightly shorter lower third.',
        },
        eyes: {
          score: 8.5,
          details: { canthalTilt: '+4°', ipd: '64mm', pfl: '28mm' },
          analysis: 'Positive canthal tilt with well-proportioned eye spacing.',
        },
        nose: {
          score: 7.2,
          details: { nasofrontalAngle: '135°', nasolabialAngle: '95°', widthRatio: '0.25' },
          analysis: 'Well-defined nasal bridge with appropriate angles.',
        },
        jawChin: {
          score: 7.8,
          details: { gonialAngle: '125°', jawWidth: '125mm', chinProjection: 'adequate' },
          analysis: 'Strong jawline definition with good chin projection.',
        },
        lipsMouth: {
          score: 8.0,
          details: { lipRatio: '1:1.4', mouthWidth: '50mm', vermilionShow: 'adequate' },
          analysis: 'Balanced lip proportions with good vermilion display.',
        },
        skinQuality: {
          score: 7.5,
          details: { clarity: 'good', texture: 'smooth', evenness: '85%' },
          analysis: 'Good skin quality with minor areas for improvement.',
        },
      },
      recommendations: [
        {
          category: 'Skincare',
          priority: 'medium' as const,
          title: 'Enhance Skin Clarity',
          description: 'Consider adding a vitamin C serum and regular exfoliation to your routine.',
        },
        {
          category: 'Grooming',
          priority: 'low' as const,
          title: 'Eyebrow Grooming',
          description: 'Light shaping could enhance your eye area score further.',
        },
        {
          category: 'Lifestyle',
          priority: 'medium' as const,
          title: 'Facial Exercises',
          description: 'Mewing and jawline exercises may enhance lower third definition.',
        },
      ],
      createdAt: new Date(),
    };

    setAnalysisResult(mockResult);
    setIsAnalyzing(false);
  };

