'use client';

import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ChevronDown, LucideIcon } from 'lucide-react';

interface ScoreCardProps {
  icon: LucideIcon;
  label: string;
  score: number;
  details: Record<string, number | string>;
  analysis: string;
  delay?: number;
}

export default function ScoreCard({
  icon: Icon,
  label,
  score,
  details,
  analysis,
  delay = 0,
}: ScoreCardProps) {
  const [isExpanded, setIsExpanded] = useState(false);

  const getScoreColor = (score: number) => {
    if (score >= 8) return 'text-green-400 bg-green-500/20';
    if (score >= 6) return 'text-blue-400 bg-blue-500/20';
    if (score >= 4) return 'text-amber-400 bg-amber-500/20';
    return 'text-red-400 bg-red-500/20';
  };

  const getBarColor = (score: number) => {
    if (score >= 8) return 'bg-green-500';
    if (score >= 6) return 'bg-blue-500';
    if (score >= 4) return 'bg-amber-500';
    return 'bg-red-500';
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay }}
      className="bg-gradient-to-br from-gray-800/50 to-gray-900/50 rounded-xl border border-white/10 overflow-hidden"
    >
      <button
        onClick={() => setIsExpanded(!isExpanded)}
        className="w-full p-4 flex items-center gap-4 hover:bg-white/5 transition-colors"
      >
        <div className="w-10 h-10 rounded-lg bg-white/10 flex items-center justify-center flex-shrink-0">
          <Icon className="w-5 h-5 text-gray-400" />
        </div>

        <div className="flex-1 text-left">
          <h4 className="font-medium text-sm mb-1">{label}</h4>
          <div className="w-full h-1.5 bg-gray-700 rounded-full overflow-hidden">
            <motion.div
              initial={{ width: 0 }}
              animate={{ width: `${(score / 10) * 100}%` }}
              transition={{ delay: delay + 0.2, duration: 0.5 }}
              className={`h-full rounded-full ${getBarColor(score)}`}
            />
          </div>
        </div>

        <div className={`px-3 py-1.5 rounded-lg ${getScoreColor(score)}`}>
          <span className="font-bold">{score.toFixed(1)}</span>
        </div>

        <ChevronDown
          className={`w-5 h-5 text-gray-400 transition-transform ${
            isExpanded ? 'rotate-180' : ''
          }`}
        />
      </button>

      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.2 }}
            className="overflow-hidden"
          >
            <div className="px-4 pb-4 pt-2 border-t border-white/5">
              <p className="text-gray-400 text-sm mb-3">{analysis}</p>

              <div className="grid grid-cols-2 gap-2">
                {Object.entries(details).map(([key, value]) => (
                  <div
                    key={key}
                    className="bg-white/5 rounded-lg px-3 py-2"
                  >
                    <span className="text-gray-500 text-xs capitalize">
                      {key.replace(/([A-Z])/g, ' $1').trim()}
                    </span>
                    <p className="text-sm font-medium font-mono">{value}</p>
                  </div>
                ))}
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
}
