     1→'use client';
     2→
     3→import { useState, useCallback, useEffect } from 'react';
     4→import { motion, AnimatePresence } from 'framer-motion';
     5→import {
     6→  BookOpen,
     7→  Dumbbell,
     8→  Sparkles,
     9→  Clock,
    10→  ChevronRight,
    11→  Search,
    12→  Brain,
    13→  Wrench,
    14→  TrendingDown,
    15→  Calendar,
    16→  Target,
    17→  Heart,
    18→  Utensils,
    19→  Droplet,
    20→  X,
    21→  ArrowLeft,
    22→  MessageSquare,
    23→  ExternalLink,
    24→  ShoppingCart,
    25→  CheckCircle2,
    26→} from 'lucide-react';
    27→import Image from 'next/image';
    28→import Link from 'next/link';
    29→import { TabContent } from '../ResultsLayout';
    30→import {
    31→  GUIDE_CATEGORIES,
    32→  getGuidesByCategory,
    33→  getTotalGuideCount,
    34→  getTotalReadTime,
    35→  getGuideProductById,
    36→  getGuidesByGender,
    37→} from '@/data/guides';
    38→import { Guide, GuideCategory, GuideMedia } from '@/types/guides';
    39→import { useRegion } from '@/contexts/RegionContext';
    40→import { useResults } from '@/contexts/ResultsContext';
    41→import ReactMarkdown from 'react-markdown';
    42→import remarkGfm from 'remark-gfm';
    43→
    44→// ============================================
    45→// ICON MAPPING
    46→// ============================================
    47→
    48→const ICON_MAP: Record<string, React.ReactNode> = {
    49→  BookOpen: <BookOpen size={20} />,
    50→  Dumbbell: <Dumbbell size={20} />,
    51→  Sparkles: <Sparkles size={20} />,
    52→  Brain: <Brain size={20} />,
    53→  Wrench: <Wrench size={20} />,
    54→  TrendingDown: <TrendingDown size={20} />,
    55→  Calendar: <Calendar size={20} />,
    56→  Target: <Target size={20} />,
    57→  Heart: <Heart size={20} />,
    58→  Utensils: <Utensils size={20} />,
    59→  Droplet: <Droplet size={20} />,
    60→};
    61→
    62→function getIconComponent(iconName: string, size = 20): React.ReactNode {
    63→  // Check icon map first
    64→  const mappedIcon = ICON_MAP[iconName];
    65→  if (mappedIcon) return mappedIcon;
    66→
    67→  // Fallback to BookOpen
    68→  return <BookOpen size={size} />;
    69→}
    70→
    71→// ============================================
    72→// MEDIA RENDERER
    73→// ============================================
    74→
    75→interface MediaRendererProps {
    76→  media: GuideMedia;
    77→}
    78→
    79→function MediaRenderer({ media }: MediaRendererProps) {
    80→  const [hasError, setHasError] = useState(false);
    81→  const [isLoading, setIsLoading] = useState(true);
    82→
    83→  const handleError = useCallback(() => {
    84→    setHasError(true);
    85→    setIsLoading(false);
    86→  }, []);
    87→
    88→  const handleLoad = useCallback(() => {
    89→    setIsLoading(false);
    90→  }, []);
    91→
    92→  const placementClasses = {
    93→    inline: 'max-w-lg mx-auto',
    94→    hero: 'w-full',
    95→    'full-width': 'w-full',
    96→  };
    97→
    98→  const isGif = media.type === 'gif' || media.url.endsWith('.gif');
    99→
   100→  // Show styled placeholder if image failed to load or doesn't exist
   101→  if (hasError) {
   102→    return (
   103→      <figure className={`my-10 ${placementClasses[media.placement || 'inline']}`}>
   104→        <div className="relative rounded-2xl overflow-hidden bg-neutral-900/40 border border-white/5">
   105→          <div className="aspect-video flex items-center justify-center p-8">
   106→            <div className="text-center max-w-sm">
   107→              <div className="w-16 h-16 bg-cyan-500/10 rounded-2xl flex items-center justify-center mx-auto mb-4 border border-cyan-500/20">
   108→                {isGif ? (
   109→                  <div className="w-6 h-6 border-2 border-cyan-400 border-t-transparent rounded-full animate-spin" />
   110→                ) : (
   111→                  <Sparkles size={28} className="text-cyan-400" />
   112→                )}
   113→              </div>
   114→              <p className="text-neutral-300 font-black uppercase tracking-wider text-sm mb-2">{media.alt}</p>
   115→              {media.caption && (
   116→                <p className="text-neutral-600 text-xs">{media.caption}</p>
   117→              )}
   118→            </div>
   119→          </div>
   120→        </div>
   121→      </figure>
   122→    );
   123→  }
   124→
   125→  return (
   126→    <figure className={`my-10 ${placementClasses[media.placement || 'inline']}`}>
   127→      <div className="relative rounded-2xl overflow-hidden bg-neutral-900/40 border border-white/5">
   128→        {/* Loading skeleton */}
   129→        {isLoading && (
   130→          <div className="absolute inset-0 bg-neutral-900/40 animate-pulse flex items-center justify-center">
   131→            <div className="w-8 h-8 border-2 border-cyan-500 border-t-transparent rounded-full animate-spin" />
   132→          </div>
   133→        )}
   134→
   135→        {isGif ? (
   136→          // Use img for GIFs to preserve animation
   137→          // eslint-disable-next-line @next/next/no-img-element
   138→          <img
   139→            src={media.url}
   140→            alt={media.alt}
   141→            className={`w-full h-auto max-h-[400px] object-contain transition-opacity duration-300 ${isLoading ? 'opacity-0' : 'opacity-100'}`}
   142→            loading="lazy"
   143→            onError={handleError}
   144→            onLoad={handleLoad}
   145→          />
   146→        ) : (
   147→          <Image
   148→            src={media.url}
   149→            alt={media.alt}
   150→            width={media.width || 800}
   151→            height={media.height || 450}
   152→            className={`w-full h-auto max-h-[400px] object-contain transition-opacity duration-300 ${isLoading ? 'opacity-0' : 'opacity-100'}`}
   153→            onError={handleError}
   154→            onLoad={handleLoad}
   155→          />
   156→        )}
   157→      </div>
   158→      {media.caption && (
   159→        <figcaption className="mt-4 text-center text-[10px] font-black uppercase tracking-widest text-neutral-600">
   160→          {media.caption}
   161→        </figcaption>
   162→      )}
   163→    </figure>
   164→  );
   165→}
   166→
   167→// ============================================
   168→// PRODUCT CALLOUT (CONVERSION OPTIMIZED)
   169→// ============================================
   170→
   171→interface ProductCalloutProps {
   172→  productId: string;
   173→}
   174→
   175→function ProductCallout({ productId }: ProductCalloutProps) {
   176→  const { getLink } = useRegion();
   177→  const product = getGuideProductById(productId);
   178→  const [hasError, setHasError] = useState(false);
   179→  const [isLoading, setIsLoading] = useState(true);
   180→
   181→  if (!product) return null;
   182→
   183→  const link = getLink(product.regionLinks, product.directLink);
   184→
   185→  return (
   186→    <div className="my-6 p-5 rounded-2xl bg-neutral-900/40 border border-white/5 hover:border-cyan-500/20 transition-all">
   187→      <div className="flex items-center gap-4">
   188→        {/* Product Image */}
   189→        <div className="w-16 h-16 rounded-xl bg-neutral-900 border border-white/10 overflow-hidden flex-shrink-0 relative">
   190→          {isLoading && !hasError && product.imageUrl && (
   191→            <div className="absolute inset-0 bg-neutral-900/60 animate-pulse flex items-center justify-center z-10">
   192→              <div className="w-4 h-4 border-2 border-cyan-500 border-t-transparent rounded-full animate-spin" />
   193→            </div>
   194→          )}
   195→          {hasError || !product.imageUrl ? (
   196→            <div className="w-full h-full flex items-center justify-center bg-neutral-900">
   197→              <ShoppingCart size={20} className="text-neutral-600" />
   198→            </div>
   199→          ) : (
   200→            <Image
   201→              src={product.imageUrl}
   202→              alt={product.name}
   203→              width={64}
   204→              height={64}
   205→              className={`w-full h-full object-cover transition-opacity ${isLoading ? 'opacity-0' : 'opacity-100'}`}
   206→              onLoad={() => setIsLoading(false)}
   207→              onError={() => { setHasError(true); setIsLoading(false); }}
   208→              unoptimized
   209→            />
   210→          )}
   211→        </div>
   212→
   213→        {/* Product Info */}
   214→        <div className="flex-1 min-w-0">
   215→          <div className="flex items-center gap-2 mb-1">
   216→            <h4 className="font-black uppercase tracking-wide text-white truncate">{product.name}</h4>
   217→            {product.brand && (
   218→              <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600">{product.brand}</span>
   219→            )}
   220→          </div>
   221→          <p className="text-sm text-cyan-400 italic">&ldquo;{product.tagline}&rdquo;</p>
   222→        </div>
   223→
   224→        {/* CTA Button */}
   225→        <a
   226→          href={link}
   227→          target="_blank"
   228→          rel="noopener noreferrer"
   229→          className="flex-shrink-0 flex items-center gap-2 px-5 py-2.5 rounded-xl bg-cyan-500 hover:bg-cyan-400 text-black font-black uppercase tracking-wider text-xs transition-colors"
   230→        >
   231→          <ShoppingCart size={14} />
   232→          <span className="hidden sm:inline">BUY</span>
   233→        </a>
   234→      </div>
   235→    </div>
   236→  );
   237→}
   238→
   239→// ============================================
   240→// FORUM DISCUSSION LINK
   241→// ============================================
   242→
   243→interface ForumDiscussionLinkProps {
   244→  forumCategory?: string;
   245→  guideTitle: string;
   246→}
   247→
   248→function ForumDiscussionLink({ forumCategory, guideTitle }: ForumDiscussionLinkProps) {
   249→  if (!forumCategory) return null;
   250→
   251→  return (
   252→    <div className="mt-8 p-6 rounded-2xl bg-neutral-900/40 border border-white/5">
   253→      <div className="flex items-center gap-3 mb-4">
   254→        <div className="w-10 h-10 rounded-xl bg-purple-500/10 border border-purple-500/20 flex items-center justify-center">
   255→          <MessageSquare size={20} className="text-purple-400" />
   256→        </div>
   257→        <div>
   258→          <h3 className="font-black uppercase tracking-wide text-white">DISCUSS THIS GUIDE</h3>
   259→          <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-600">Join the community conversation</p>
   260→        </div>
   261→      </div>
   262→
   263→      <p className="text-sm text-neutral-400 mb-5">
   264→        Have questions about {guideTitle.toLowerCase()}? Want to share your experience?
   265→        Join the discussion in our community forum.
   266→      </p>
   267→
   268→      <Link
   269→        href={`/forum/${forumCategory}`}
   270→        className="inline-flex items-center gap-2 px-5 py-2.5 rounded-xl bg-neutral-900/50 border border-white/5 hover:border-purple-500/30 text-purple-400 font-black uppercase tracking-wider text-xs transition-all"
   271→      >
   272→        <MessageSquare size={14} />
   273→        GO TO FORUM
   274→        <ExternalLink size={12} />
   275→      </Link>
   276→    </div>
   277→  );
   278→}
   279→
   280→// ============================================
   281→// GUIDE STATS CARD
   282→// ============================================
   283→
   284→function GuideStatsCard() {
   285→  const totalGuides = getTotalGuideCount();
   286→  const totalMinutes = getTotalReadTime();
   287→
   288→  return (
   289→    <motion.div
   290→      className="rounded-[2rem] bg-neutral-900/40 border border-white/5 p-6 mb-8"
   291→      initial={{ opacity: 0, y: 20 }}
   292→      animate={{ opacity: 1, y: 0 }}
   293→    >
   294→      <div className="flex items-center gap-4 mb-6">
   295→        <div className="w-12 h-12 rounded-xl bg-neutral-900 border border-white/10 flex items-center justify-center">
   296→          <BookOpen size={22} className="text-cyan-400" />
   297→        </div>
   298→        <div>
   299→          <h2 className="text-xl font-black italic uppercase tracking-tight text-white">PRODUCT <span className="text-cyan-400">GUIDES</span></h2>
   300→          <p className="text-[10px] font-black uppercase tracking-[0.2em] text-neutral-600">EVIDENCE-BASED LOOKSMAXXING RESOURCES</p>
   301→        </div>
   302→      </div>
   303→
   304→      <div className="grid grid-cols-2 gap-4">
   305→        <div className="rounded-xl bg-neutral-900/50 border border-white/5 p-4">
   306→          <div className="text-3xl font-black text-white mb-1">{totalGuides}</div>
   307→          <div className="text-[10px] font-black uppercase tracking-widest text-neutral-600">TOTAL GUIDES</div>
   308→        </div>
   309→        <div className="rounded-xl bg-neutral-900/50 border border-white/5 p-4">
   310→          <div className="flex items-center gap-2 mb-1">
   311→            <Clock size={18} className="text-cyan-400" />
   312→            <div className="text-3xl font-black text-white">{totalMinutes}</div>
   313→          </div>
   314→          <div className="text-[10px] font-black uppercase tracking-widest text-neutral-600">MINUTES OF CONTENT</div>
   315→        </div>
   316→      </div>
   317→    </motion.div>
   318→  );
   319→}
   320→
   321→// ============================================
   322→// GUIDE VIEWER MODAL
   323→// ============================================
   324→
   325→interface GuideViewerProps {
   326→  guide: Guide;
   327→  onClose: () => void;
   328→  onSwitchGuide: (guide: Guide) => void;
   329→  allGuides: Guide[];
   330→  gender: 'male' | 'female';
   331→}
   332→
   333→function GuideViewer({ guide, onClose, onSwitchGuide, allGuides, gender }: GuideViewerProps) {
   334→  const [currentSection, setCurrentSection] = useState(0);
   335→  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
   336→  const [expandedCategories, setExpandedCategories] = useState<string[]>(() => {
   337→    // Find the category of the current guide and expand it by default
   338→    const currentCategory = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guide.id));
   339→    return currentCategory ? [currentCategory.id] : [];
   340→  });
   341→
   342→  // Reset to first section when switching guides
   343→  useEffect(() => {
   344→    setCurrentSection(0);
   345→    // Also expand the new guide's category
   346→    const newCategory = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guide.id));
   347→    if (newCategory) {
   348→      setExpandedCategories(prev =>
   349→        prev.includes(newCategory.id) ? prev : [...prev, newCategory.id]
   350→      );
   351→    }
   352→  }, [guide.id]);
   353→
   354→  const section = guide.sections[currentSection];
   355→
   356→  if (!section) return null;
   357→
   358→  // Calculate reading progress (0% at start, 100% at last section)
   359→  const progress = guide.sections.length > 1
   360→    ? (currentSection / (guide.sections.length - 1)) * 100
   361→    : 100;
   362→
   363→  const toggleCategory = (categoryId: string) => {
   364→    setExpandedCategories(prev =>
   365→      prev.includes(categoryId)
   366→        ? prev.filter(id => id !== categoryId)
   367→        : [...prev, categoryId]
   368→    );
   369→  };
   370→
   371→  // Filter categories by gender
   372→  const filteredCategories = GUIDE_CATEGORIES.filter(cat => {
   373→    if (cat.id === 'male' && gender === 'female') return false;
   374→    if (cat.id === 'female' && gender === 'male') return false;
   375→    return true;
   376→  });
   377→
   378→  const colorClasses: Record<string, { text: string; bg: string; border: string }> = {
   379→    blue: { text: 'text-cyan-400', bg: 'bg-cyan-500/10', border: 'border-cyan-500/20' },
   380→    purple: { text: 'text-purple-400', bg: 'bg-purple-500/10', border: 'border-purple-500/20' },
   381→    amber: { text: 'text-amber-400', bg: 'bg-amber-500/10', border: 'border-amber-500/20' },
   382→    pink: { text: 'text-pink-400', bg: 'bg-pink-500/10', border: 'border-pink-500/20' },
   383→    emerald: { text: 'text-emerald-400', bg: 'bg-emerald-500/10', border: 'border-emerald-500/20' },
   384→    red: { text: 'text-red-400', bg: 'bg-red-500/10', border: 'border-red-500/20' },
   385→  };
   386→
   387→  return (
   388→    <motion.div
   389→      initial={{ opacity: 0 }}
   390→      animate={{ opacity: 1 }}
   391→      exit={{ opacity: 0 }}
   392→      className="fixed inset-0 z-50 bg-black"
   393→    >
   394→      {/* Header */}
   395→      <div className="sticky top-0 z-20 bg-black/95 backdrop-blur-md border-b border-neutral-900">
   396→        <div className="flex items-center justify-between px-4 lg:px-8 py-3">
   397→          <button
   398→            onClick={onClose}
   399→            className="flex items-center gap-2 text-neutral-500 hover:text-cyan-400 text-xs font-black uppercase tracking-widest transition-colors"
   400→          >
   401→            <ArrowLeft size={14} />
   402→            <span className="hidden sm:inline">BACK TO GUIDES</span>
   403→          </button>
   404→
   405→          {/* Progress bar */}
   406→          <div className="hidden md:flex items-center gap-4 flex-1 max-w-md mx-8">
   407→            <div className="flex-1 h-1 bg-neutral-900 rounded-full overflow-hidden">
   408→              <motion.div
   409→                className="h-full bg-gradient-to-r from-cyan-500 to-cyan-400"
   410→                initial={{ width: 0 }}
   411→                animate={{ width: `${progress}%` }}
   412→                transition={{ duration: 0.3 }}
   413→              />
   414→            </div>
   415→            <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600 whitespace-nowrap">
   416→              {currentSection + 1} / {guide.sections.length}
   417→            </span>
   418→          </div>
   419→
   420→          <div className="flex items-center gap-3">
   421→            {/* Mobile menu button */}
   422→            <button
   423→              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
   424→              className="lg:hidden p-2 rounded-xl bg-neutral-900/50 border border-white/5 hover:border-white/10 transition-colors"
   425→            >
   426→              <BookOpen size={18} className="text-neutral-400" />
   427→            </button>
   428→            <button
   429→              onClick={onClose}
   430→              className="p-2 rounded-xl bg-neutral-900/50 border border-white/5 hover:border-white/10 transition-colors"
   431→            >
   432→              <X size={18} className="text-neutral-400" />
   433→            </button>
   434→          </div>
   435→        </div>
   436→      </div>
   437→
   438→      {/* Main Layout: Sidebar + Content */}
   439→      <div className="flex h-[calc(100vh-57px)]">
   440→        {/* Sidebar Navigation */}
   441→        <aside
   442→          className={`
   443→            fixed lg:relative inset-y-0 left-0 z-30 lg:z-0
   444→            w-72 lg:w-80 bg-black lg:bg-neutral-950/50
   445→            border-r border-neutral-900
   446→            transform transition-transform duration-300 ease-in-out
   447→            ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'}
   448→            flex flex-col
   449→          `}
   450→        >
   451→          {/* Sidebar Header */}
   452→          <div className="p-4 border-b border-neutral-900">
   453→            <div className="text-[10px] font-black uppercase tracking-[0.4em] text-neutral-600 mb-2">
   454→              ALL GUIDES
   455→            </div>
   456→            <p className="text-xs text-neutral-500">Select a lesson below</p>
   457→          </div>
   458→
   459→          {/* Categories & Guides List */}
   460→          <nav className="flex-1 overflow-y-auto p-3">
   461→            {filteredCategories.map(category => {
   462→              const categoryGuides = allGuides.filter(g => category.guideIds.includes(g.id));
   463→              if (categoryGuides.length === 0) return null;
   464→
   465→              const isExpanded = expandedCategories.includes(category.id);
   466→              const styles = colorClasses[category.color] || colorClasses.blue;
   467→              const hasCurrentGuide = categoryGuides.some(g => g.id === guide.id);
   468→
   469→              return (
   470→                <div key={category.id} className="mb-2">
   471→                  {/* Category Header */}
   472→                  <button
   473→                    onClick={() => toggleCategory(category.id)}
   474→                    className={`
   475→                      w-full text-left px-3 py-3 rounded-xl transition-all
   476→                      flex items-center gap-3 group
   477→                      ${hasCurrentGuide ? `${styles.bg} border ${styles.border}` : 'hover:bg-neutral-900/50 border border-transparent'}
   478→                    `}
   479→                  >
   480→                    <div className={`w-8 h-8 rounded-xl ${styles.bg} border ${styles.border} flex items-center justify-center ${styles.text} flex-shrink-0`}>
   481→                      {getIconComponent(category.icon, 16)}
   482→                    </div>
   483→                    <div className="flex-1 min-w-0">
   484→                      <span className={`text-xs font-black uppercase tracking-wide block ${hasCurrentGuide ? 'text-white' : 'text-neutral-400 group-hover:text-white'}`}>
   485→                        {category.name}
   486→                      </span>
   487→                      <span className="text-[10px] text-neutral-600">
   488→                        {categoryGuides.length} guide{categoryGuides.length !== 1 ? 's' : ''}
   489→                      </span>
   490→                    </div>
   491→                    <ChevronRight
   492→                      size={14}
   493→                      className={`${styles.text} transition-transform ${isExpanded ? 'rotate-90' : ''}`}
   494→                    />
   495→                  </button>
   496→
   497→                  {/* Guides List (collapsible) */}
   498→                  <AnimatePresence>
   499→                    {isExpanded && (
   500→                      <motion.ul
   501→                        initial={{ height: 0, opacity: 0 }}
   502→                        animate={{ height: 'auto', opacity: 1 }}
   503→                        exit={{ height: 0, opacity: 0 }}
   504→                        transition={{ duration: 0.2 }}
   505→                        className="overflow-hidden ml-4 mt-1 space-y-0.5"
   506→                      >
   507→                        {categoryGuides.map(g => {
   508→                          const isCurrentGuide = g.id === guide.id;
   509→                          return (
   510→                            <li key={g.id}>
   511→                              <button
   512→                                onClick={() => {
   513→                                  if (isCurrentGuide) {
   514→                                    // Already on this guide, just close sidebar on mobile
   515→                                    setIsSidebarOpen(false);
   516→                                  } else {
   517→                                    onSwitchGuide(g);
   518→                                    setIsSidebarOpen(false);
   519→                                  }
   520→                                }}
   521→                                className={`
   522→                                  w-full text-left px-3 py-2 rounded-lg transition-all
   523→                                  flex items-center gap-2 group
   524→                                  ${isCurrentGuide
   525→                                    ? `${styles.bg} border ${styles.border}`
   526→                                    : 'hover:bg-neutral-900/30 border border-transparent'
   527→                                  }
   528→                                `}
   529→                              >
   530→                                <span className={`w-1.5 h-1.5 rounded-full flex-shrink-0 ${isCurrentGuide ? styles.text.replace('text-', 'bg-') : 'bg-neutral-700'}`} />
