   919→// ============================================
   920→// GUIDE CARD
   921→// ============================================
   922→
   923→interface GuideCardProps {
   924→  guide: Guide;
   925→  index: number;
   926→  onOpen: (guide: Guide) => void;
   927→}
   928→
   929→function GuideCard({ guide, index, onOpen }: GuideCardProps) {
   930→  const getCategoryColor = (guideId: string) => {
   931→    const category = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guideId));
   932→    if (!category) return 'cyan';
   933→    const colorMap: Record<string, string> = {
   934→      blue: 'cyan',
   935→      purple: 'purple',
   936→      amber: 'amber',
   937→      pink: 'pink',
   938→      emerald: 'emerald',
   939→      red: 'red',
   940→    };
   941→    return colorMap[category.color] || 'cyan';
   942→  };
   943→
   944→  const color = getCategoryColor(guide.id);
   945→
   946→  const colorClasses: Record<string, { bg: string; border: string; text: string; iconBg: string }> = {
   947→    cyan: {
   948→      bg: 'bg-neutral-900/40',
   949→      border: 'border-white/5 hover:border-cyan-500/20',
   950→      text: 'text-cyan-400',
   951→      iconBg: 'bg-neutral-900 border border-white/10',
   952→    },
   953→    purple: {
   954→      bg: 'bg-neutral-900/40',
   955→      border: 'border-white/5 hover:border-purple-500/20',
   956→      text: 'text-purple-400',
   957→      iconBg: 'bg-neutral-900 border border-white/10',
   958→    },
   959→    amber: {
   960→      bg: 'bg-neutral-900/40',
   961→      border: 'border-white/5 hover:border-amber-500/20',
   962→      text: 'text-amber-400',
   963→      iconBg: 'bg-neutral-900 border border-white/10',
   964→    },
   965→    pink: {
   966→      bg: 'bg-neutral-900/40',
   967→      border: 'border-white/5 hover:border-pink-500/20',
   968→      text: 'text-pink-400',
   969→      iconBg: 'bg-neutral-900 border border-white/10',
   970→    },
   971→    emerald: {
   972→      bg: 'bg-neutral-900/40',
   973→      border: 'border-white/5 hover:border-emerald-500/20',
   974→      text: 'text-emerald-400',
   975→      iconBg: 'bg-neutral-900 border border-white/10',
   976→    },
   977→    red: {
   978→      bg: 'bg-neutral-900/40',
   979→      border: 'border-white/5 hover:border-red-500/20',
   980→      text: 'text-red-400',
   981→      iconBg: 'bg-neutral-900 border border-white/10',
   982→    },
   983→  };
   984→
   985→  const styles = colorClasses[color] || colorClasses.cyan;
   986→
   987→  return (
   988→    <motion.div
   989→      onClick={() => onOpen(guide)}
   990→      className={`${styles.bg} ${styles.border} border rounded-2xl p-5 transition-all cursor-pointer group`}
   991→      initial={{ opacity: 0, y: 20 }}
   992→      animate={{ opacity: 1, y: 0 }}
   993→      transition={{ delay: index * 0.05 }}
   994→      whileHover={{ y: -2 }}
   995→    >
   996→      <div className="flex items-start justify-between mb-4">
   997→        <div className={`w-10 h-10 ${styles.iconBg} rounded-xl flex items-center justify-center ${styles.text}`}>
   998→          {getIconComponent(guide.icon)}
   999→        </div>
  1000→        <div className="flex items-center gap-1.5 px-2 py-1 rounded-lg bg-neutral-900/50 border border-white/5">
  1001→          <Clock size={10} className="text-neutral-600" />
  1002→          <span className="text-[10px] font-black uppercase tracking-wider text-neutral-500">{guide.estimatedReadTime} MIN</span>
  1003→        </div>
  1004→      </div>
  1005→
  1006→      <h3 className="font-black uppercase tracking-tight text-white mb-1 group-hover:text-cyan-400 transition-colors">
  1007→        {guide.title}
  1008→      </h3>
  1009→
  1010→      {guide.subtitle && (
  1011→        <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-600 mb-2">{guide.subtitle}</p>
  1012→      )}
  1013→
  1014→      <p className="text-sm text-neutral-500 mb-4 line-clamp-2 font-medium">{guide.description}</p>
  1015→
  1016→      <div className="flex items-center justify-between pt-3 border-t border-white/5">
  1017→        <div className="flex items-center gap-2">
  1018→          <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600">
  1019→            {guide.sections.length} SECTIONS
  1020→          </span>
  1021→          {guide.productIds && guide.productIds.length > 0 && (
  1022→            <>
  1023→              <span className="w-1 h-1 rounded-full bg-neutral-800" />
  1024→              <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600">
  1025→                {guide.productIds.length} PRODUCTS
  1026→              </span>
  1027→            </>
  1028→          )}
  1029→        </div>
  1030→        <ChevronRight size={16} className="text-neutral-700 group-hover:text-cyan-400 transition-colors" />
  1031→      </div>
  1032→    </motion.div>
  1033→  );
  1034→}
