  1036→// ============================================
  1037→// CATEGORY SECTION
  1038→// ============================================
  1039→
  1040→interface CategorySectionProps {
  1041→  category: GuideCategory;
  1042→  onOpenGuide: (guide: Guide) => void;
  1043→  gender: 'male' | 'female';
  1044→}
  1045→
  1046→function CategorySection({ category, onOpenGuide, gender }: CategorySectionProps) {
  1047→  // Skip gender-specific categories for the opposite gender
  1048→  if (category.id === 'male' && gender === 'female') return null;
  1049→  if (category.id === 'female' && gender === 'male') return null;
  1050→
  1051→  const guides = getGuidesByCategory(category.id);
  1052→
  1053→  if (guides.length === 0) return null;
  1054→
  1055→  const colorClasses: Record<string, { text: string; iconBg: string }> = {
  1056→    blue: { text: 'text-cyan-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1057→    purple: { text: 'text-purple-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1058→    pink: { text: 'text-pink-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1059→    cyan: { text: 'text-cyan-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1060→    emerald: { text: 'text-emerald-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1061→    red: { text: 'text-red-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1062→    amber: { text: 'text-amber-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1063→  };
  1064→
  1065→  const styles = colorClasses[category.color] || colorClasses.cyan;
  1066→
  1067→  return (
  1068→    <div className="mb-12">
  1069→      {/* Category Header */}
  1070→      <div className="flex items-center gap-4 mb-6">
  1071→        <div className={`w-10 h-10 ${styles.iconBg} rounded-xl flex items-center justify-center ${styles.text}`}>
  1072→          {getIconComponent(category.icon)}
  1073→        </div>
  1074→        <div className="flex-1">
  1075→          <h3 className="text-[10px] font-black uppercase tracking-[0.4em] text-neutral-600 flex items-center gap-4">
  1076→            {category.name}
  1077→            <div className="flex-1 h-px bg-neutral-800" />
  1078→          </h3>
  1079→          <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-700 mt-1">{category.description}</p>
  1080→        </div>
  1081→      </div>
  1082→
  1083→      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
  1084→        {guides.map((guide, idx) => (
  1085→          <GuideCard key={guide.id} guide={guide} index={idx} onOpen={onOpenGuide} />
  1086→        ))}
  1087→      </div>
  1088→    </div>
  1089→  );
  1090→}
  1091→
  1092→// ============================================
  1093→// SEARCH BAR
  1094→// ============================================
  1095→
  1096→interface SearchBarProps {
  1097→  query: string;
  1098→  onQueryChange: (query: string) => void;
  1099→}
  1100→
  1101→function SearchBar({ query, onQueryChange }: SearchBarProps) {
  1102→  return (
  1103→    <div className="relative mb-8">
  1104→      <Search size={16} className="absolute left-4 top-1/2 -translate-y-1/2 text-neutral-600" />
  1105→      <input
  1106→        type="text"
  1107→        placeholder="SEARCH GUIDES..."
  1108→        value={query}
  1109→        onChange={(e) => onQueryChange(e.target.value)}
  1110→        className="w-full bg-neutral-900/40 border border-white/5 rounded-xl pl-11 pr-4 py-3.5 text-white placeholder:text-neutral-600 placeholder:font-black placeholder:uppercase placeholder:tracking-widest placeholder:text-xs focus:outline-none focus:border-cyan-500/30 transition-colors font-medium text-sm"
  1111→      />
  1112→    </div>
