  1119→
  1120→export function GuidesTab() {
  1121→  const [searchQuery, setSearchQuery] = useState('');
  1122→  const [selectedGuide, setSelectedGuide] = useState<Guide | null>(null);
  1123→  const { gender } = useResults();
  1124→
  1125→  // Filter guides by gender (excludes opposite gender's specific guides)
  1126→  const genderFilteredGuides = getGuidesByGender(gender);
  1127→
  1128→  const filteredGuides = searchQuery.trim()
  1129→    ? genderFilteredGuides.filter(g =>
  1130→        g.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
  1131→        g.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
  1132→        (g.subtitle && g.subtitle.toLowerCase().includes(searchQuery.toLowerCase())) ||
  1133→        (g.tags && g.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase())))
  1134→      )
  1135→    : null;
  1136→
  1137→  const handleOpenGuide = (guide: Guide) => {
  1138→    setSelectedGuide(guide);
  1139→  };
  1140→
  1141→  const handleCloseGuide = () => {
