  1165→              </p>
  1166→              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
  1167→                {filteredGuides.map((guide, idx) => (
  1168→                  <GuideCard key={guide.id} guide={guide} index={idx} onOpen={handleOpenGuide} />
  1169→                ))}
  1170→              </div>
  1171→              {filteredGuides.length === 0 && (
  1172→                <div className="text-center py-16">
  1173→                  <div className="w-16 h-16 rounded-2xl bg-neutral-900 border border-white/10 flex items-center justify-center mx-auto mb-4">
  1174→                    <Search size={24} className="text-neutral-700" />
  1175→                  </div>
  1176→                  <p className="text-[10px] font-black uppercase tracking-widest text-neutral-600">NO GUIDES FOUND MATCHING YOUR SEARCH</p>
  1177→                </div>
