}

interface CategoryApiResponse {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  display_order: number;
  post_count: number;
  sub_forums: SubForumApiResponse[];
}

interface CategoryListApiResponse {
  id: string;
