/**
 * API Client for LOOKSMAXX Backend
 *
 * Handles all authenticated requests to the Railway-hosted API
 */

import type { UserRank, LeaderboardEntry, LeaderboardData, UserProfile } from '@/types/results';
import type {
  Category,
  SubForum,
  Post,
  PostListItem,
  PostListResponse,
  PostCreate,
  PostUpdate,
  Comment,
  CommentCreate,
  VoteType,
  VoteResponse,
  ReportCreate,
  Report,
  GuideSection,
  RecommendedForum,
  SortOrder,
  CategoryListItem,
  PostAuthor,
  ForumSearchResult,
  ForumSearchResponse,
  SearchResultType,
  ForumUserProfile,
  UserPostItem,
  UserPostListResponse,
  UserCommentItem,
  UserCommentListResponse,
  BookmarkToggleResponse,
  BookmarkStatus,
  BookmarkedPostsResponse,
  // Admin/Moderation types
  ReportDetail,
  AdminReportListResponse,
  ResolveReportRequest,
  ModerationStats,
  BanUserRequest,
  BanUserResponse,
  BannedUser,
  BannedUsersResponse,
  ReportStatusEnum,
  ActionTaken,
  TargetType,
  ReportReason,
  KarmaLeaderboardEntry,
  KarmaLeaderboardResponse,
} from '@/types/forum';

const API_URL = (process.env.NEXT_PUBLIC_API_URL || 'http://localhost:8000').trim();

// API Response types (snake_case from backend)
interface LeaderboardApiResponse {
  user_id: string;
  score: number;
  global_rank: number;
  gender_rank: number;
  percentile: number;
  total_users: number;
  gender_total: number;
  anonymous_name: string;
  updated_at: string;
}

interface LeaderboardEntryResponse {
  user_id: string;
  rank: number;
  score: number;
  anonymous_name: string;
  gender: 'male' | 'female';
  face_photo_url: string | null;
  is_current_user: boolean;
  top_strengths: string[];
  top_improvements: string[];
}

interface LeaderboardListResponse {
  entries: LeaderboardEntryResponse[];
  total_count: number;
  user_rank: LeaderboardApiResponse | null;
}

interface UserProfileResponse {
  user_id: string;
  rank: number;
  score: number;
  anonymous_name: string;
  gender: 'male' | 'female';
  face_photo_url: string | null;
  top_strengths: string[];
  top_improvements: string[];
}

// Transform functions (snake_case -> camelCase)
function transformUserRank(data: LeaderboardApiResponse): UserRank {
  return {
    userId: data.user_id,
    score: data.score,
    globalRank: data.global_rank,
    genderRank: data.gender_rank,
    percentile: data.percentile,
    totalUsers: data.total_users,
    genderTotal: data.gender_total,
    anonymousName: data.anonymous_name,
    updatedAt: data.updated_at,
  };
}

function transformLeaderboardEntry(data: LeaderboardEntryResponse): LeaderboardEntry {
  return {
    userId: data.user_id,
    rank: data.rank,
    score: data.score,
    anonymousName: data.anonymous_name,
    gender: data.gender,
    facePhotoUrl: data.face_photo_url,
    isCurrentUser: data.is_current_user,
    topStrengths: data.top_strengths || [],
    topImprovements: data.top_improvements || [],
  };
}

function transformLeaderboardData(data: LeaderboardListResponse): LeaderboardData {
  return {
    entries: data.entries.map(transformLeaderboardEntry),
    totalCount: data.total_count,
    userRank: data.user_rank ? transformUserRank(data.user_rank) : null,
  };
}

function transformUserProfile(data: UserProfileResponse): UserProfile {
  return {
    userId: data.user_id,
    rank: data.rank,
    score: data.score,
    anonymousName: data.anonymous_name,
    gender: data.gender,
    facePhotoUrl: data.face_photo_url,
    topStrengths: data.top_strengths,
    topImprovements: data.top_improvements,
  };
}

// === FORUM API RESPONSE TYPES ===

interface SubForumApiResponse {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  display_order: number;
  post_count: number;
}

interface CategoryApiResponse {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  display_order: number;
  post_count: number;
  sub_forums: SubForumApiResponse[];
}

interface CategoryListApiResponse {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  display_order: number;
  post_count: number;
}

interface PostAuthorApiResponse {
  id: string;
  username: string;
}

interface PostApiResponse {
  id: string;
  title: string;
  content: string;
  sub_forum_id: string;
  sub_forum_slug: string;
  category_slug: string;
  author: PostAuthorApiResponse;
  is_pinned: boolean;
  is_guide: boolean;
  vote_count: number;
  comment_count: number;
  user_vote: VoteType | null;
  created_at: string;
  updated_at: string;
}

interface PostListItemApiResponse {
  id: string;
  title: string;
  content_preview: string;
  sub_forum_slug: string;
  category_slug: string;
  author: PostAuthorApiResponse;
  is_pinned: boolean;
  is_guide: boolean;
  vote_count: number;
  comment_count: number;
  user_vote: VoteType | null;
  created_at: string;
}

interface PostListApiResponse {
  posts: PostListItemApiResponse[];
  total_count: number;
  has_more: boolean;
}

interface CommentApiResponse {
  id: string;
  content: string;
  post_id: string;
  author: PostAuthorApiResponse;
  parent_id: string | null;
  vote_count: number;
  user_vote: VoteType | null;
  depth: number;
  replies: CommentApiResponse[];
  created_at: string;
  updated_at: string;
}

interface VoteApiResponse {
  success: boolean;
  new_vote_count: number;
  user_vote: VoteType | null;
}

interface ReportApiResponse {
  id: string;
  target_type: 'post' | 'comment';
  target_id: string;
  reason: string;
  status: string;
  created_at: string;
}

interface GuideSectionApiResponse {
  category: CategoryListApiResponse;
  guides: PostListItemApiResponse[];
}

interface RecommendedForumApiResponse {
  category: CategoryListApiResponse;
  matched_flaws: string[];
  priority: number;
}

interface ArchetypeForumRecommendationApiResponse {
  category: CategoryListApiResponse;
  archetype: string;
  reason: string | null;
  priority: number;
}

interface ForumSearchResultApiResponse {
  id: string;
  type: 'post' | 'comment';
  title: string | null;
  content_preview: string;
  post_id: string | null;
  post_title: string | null;
  author: PostAuthorApiResponse;
  vote_count: number;
  category_slug: string;
  sub_forum_slug: string;
  created_at: string;
}

interface ForumSearchApiResponse {
  results: ForumSearchResultApiResponse[];
  total_count: number;
  query: string;
  search_type: string;
  has_more: boolean;
}

// === USER PROFILE API RESPONSE TYPES ===

interface ForumUserProfileApiResponse {
  id: string;
  username: string;
  forum_posts_count: number;
  forum_comments_count: number;
  forum_karma: number;
  member_since: string;
}

interface UserPostItemApiResponse {
  id: string;
  title: string;
  content_preview: string;
  sub_forum_slug: string;
  category_slug: string;
  is_pinned: boolean;
  is_guide: boolean;
  vote_count: number;
  comment_count: number;
  user_vote: VoteType | null;
  created_at: string;
}

interface UserPostListApiResponse {
  posts: UserPostItemApiResponse[];
  total_count: number;
  has_more: boolean;
}

interface UserCommentItemApiResponse {
  id: string;
  content: string;
  post_id: string;
  post_title: string;
  category_slug: string;
  vote_count: number;
  user_vote: VoteType | null;
  created_at: string;
}

interface UserCommentListApiResponse {
  comments: UserCommentItemApiResponse[];
  total_count: number;
  has_more: boolean;
}

// === BOOKMARK API RESPONSE TYPES ===

interface BookmarkToggleApiResponse {
  is_bookmarked: boolean;
  message: string;
}

interface BookmarkStatusApiResponse {
  post_id: string;
  is_bookmarked: boolean;
  bookmarked_at: string | null;
}

interface BookmarkedPostsApiResponse {
  posts: PostListItemApiResponse[];
  total_count: number;
  has_more: boolean;
}

// === FORUM TRANSFORM FUNCTIONS ===

function transformSubForum(data: SubForumApiResponse): SubForum {
  return {
    id: data.id,
    name: data.name,
    slug: data.slug,
    description: data.description,
    icon: data.icon,
    displayOrder: data.display_order,
    postCount: data.post_count,
  };
}

function transformCategory(data: CategoryApiResponse): Category {
  return {
    id: data.id,
