'use client';

import { Award } from 'lucide-react';
import { getKarmaTier, getKarmaTierColor, getKarmaTierBgColor, getKarmaTierLabel, type KarmaTier } from '@/types/forum';

interface KarmaBadgeProps {
  karma: number;
  size?: 'xs' | 'sm' | 'md';
  showLabel?: boolean;
  showIcon?: boolean;
}

/**
 * Karma badge component that displays user karma with tier-based styling
 *
 * Tiers:
 * - Bronze: 0-99
 * - Silver: 100-499
 * - Gold: 500-999
 * - Platinum: 1000-4999
 * - Diamond: 5000+
 */
export function KarmaBadge({
  karma,
  size = 'sm',
  showLabel = false,
  showIcon = true
}: KarmaBadgeProps) {
  const tier = getKarmaTier(karma);
  const tierColor = getKarmaTierColor(tier);
  const tierBgColor = getKarmaTierBgColor(tier);
  const tierLabel = getKarmaTierLabel(tier);

  const sizeClasses = {
    xs: 'text-[8px] px-1.5 py-0.5 gap-0.5',
    sm: 'text-[10px] px-2 py-0.5 gap-1',
    md: 'text-xs px-2.5 py-1 gap-1.5',
  };

  const iconSizes = {
    xs: 8,
    sm: 10,
    md: 12,
  };

  return (
    <span
      className={`inline-flex items-center font-black uppercase tracking-wider rounded-md border ${tierBgColor} ${tierColor} ${sizeClasses[size]}`}
      title={`${tierLabel} tier - ${karma.toLocaleString()} karma`}
    >
      {showIcon && <Award size={iconSizes[size]} />}
      {showLabel ? (
        <span>{tierLabel}</span>
      ) : (
        <span>{karma.toLocaleString()}</span>
      )}
    </span>
  );
}

/**
 * Inline karma display for use in post/comment author lines
 */
interface InlineKarmaProps {
  karma: number;
  className?: string;
}

export function InlineKarma({ karma, className = '' }: InlineKarmaProps) {
  const tier = getKarmaTier(karma);
  const tierColor = getKarmaTierColor(tier);

  return (
    <span
      className={`text-[9px] font-bold ${tierColor} ${className}`}
      title={`${karma.toLocaleString()} karma`}
    >
      {karma.toLocaleString()}
    </span>
  );
}

/**
 * Large karma card for profile pages
 */
interface KarmaCardProps {
  karma: number;
  postsCount: number;
  commentsCount: number;
}

export function KarmaCard({ karma, postsCount, commentsCount }: KarmaCardProps) {
  const tier = getKarmaTier(karma);
  const tierColor = getKarmaTierColor(tier);
  const tierBgColor = getKarmaTierBgColor(tier);
  const tierLabel = getKarmaTierLabel(tier);

  return (
    <div className={`p-4 rounded-xl border ${tierBgColor}`}>
      <div className="flex items-center gap-3 mb-3">
        <div className={`p-2 rounded-lg ${tierBgColor}`}>
          <Award size={20} className={tierColor} />
        </div>
        <div>
          <p className={`text-lg font-black ${tierColor}`}>
            {karma.toLocaleString()}
          </p>
          <p className="text-[10px] font-bold uppercase tracking-widest text-neutral-500">
            {tierLabel} Tier
          </p>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-3 text-center">
        <div className="p-2 rounded-lg bg-neutral-900/30">
          <p className="text-sm font-bold text-white">{postsCount}</p>
          <p className="text-[9px] uppercase tracking-wider text-neutral-500">Posts</p>
        </div>
        <div className="p-2 rounded-lg bg-neutral-900/30">
          <p className="text-sm font-bold text-white">{commentsCount}</p>
          <p className="text-[9px] uppercase tracking-wider text-neutral-500">Comments</p>
        </div>
      </div>
    </div>
  );
}

