   590→              }`}
   591→            >
   592→              <Crosshair className="w-4 h-4" />
   593→              {autoZoomEnabled ? 'Auto-Zoom: ON' : 'Auto-Zoom: OFF'}
   594→            </button>
   595→
   596→            {/* Toggle & Skip */}
   597→            <div className="flex gap-2">
   598→              <button
   599→                onClick={() => setShowAllPoints(!showAllPoints)}
