   604→                }`}
   605→              >
   606→                {showAllPoints ? <Eye className="w-4 h-4" /> : <EyeOff className="w-4 h-4" />}
   607→                {showAllPoints ? 'All Points' : 'Current Only'}
   608→              </button>
   609→              <button
   610→                onClick={() => onComplete?.(landmarks)}
   611→                className="py-2.5 px-4 rounded-lg text-xs font-medium bg-neutral-800 text-neutral-400 hover:bg-neutral-700 transition-all"
   612→              >
   613→                Skip All
   614→              </button>
   615→            </div>
   616→          </div>
   617→        </div>
   618→
   619→        {/* Main Image Area */}
   620→        <div className="flex-1 min-h-0 relative md:order-1 flex flex-col">
   621→          <div
   622→            ref={containerRef}
   623→            className="relative flex-1 bg-neutral-900 overflow-hidden rounded-xl border border-neutral-800 select-none"
   624→            onClick={handleContainerClick}
   625→            onMouseDown={handleMouseDown}
   626→            onMouseMove={handleMouseMove}
   627→            onMouseUp={handleMouseUp}
   628→            onMouseLeave={handleMouseUp}
   629→            style={{
   630→              cursor: zoomLevel > 1 && isDragging ? 'grabbing' : zoomLevel > 1 ? 'grab' : 'crosshair',
   631→            }}
   632→          >
   633→            {/* Transformed Content */}
   634→            <div
   635→              className="absolute inset-0 transition-transform duration-100"
   636→              style={{
