   728→            {/* Zoom indicator */}
   729→            {zoomLevel > 1 && (
   730→              <div className="absolute top-3 left-3 px-3 py-1.5 rounded-lg bg-black/80 border border-neutral-700 text-white text-xs">
   731→                {zoomLevel}x zoom - Drag to pan
   732→              </div>
   733→            )}
   734→
   735→            {/* Current landmark label overlay */}
   736→            <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black via-black/80 to-transparent p-4 pointer-events-none">
   737→              <p className="text-center text-white text-sm font-medium">
   738→                Click to place:{' '}
   739→                <span className="font-semibold" style={{ color }}>{currentLandmark?.label}</span>
   740→              </p>
   741→            </div>
   742→          </div>
   743→
   744→          {/* Navigation Bar - Fixed at bottom of image area */}
   745→          <div className="mt-3 flex items-center justify-center gap-3">
   746→            <button
   747→              onClick={handlePrevious}
   748→              className="p-2.5 rounded-xl bg-neutral-800 text-neutral-300 hover:bg-neutral-700 border border-neutral-700 transition-all"
