   759→                <div
   760→                  className="h-full bg-[#00f3ff] rounded-full transition-all duration-200"
   761→                  style={{ width: `${((currentStepIndex + 1) / totalSteps) * 100}%` }}
   762→                />
   763→              </div>
   764→              <span className="text-xs text-neutral-400 font-mono min-w-[4ch] text-right">
   765→                {totalSteps}
   766→              </span>
