   772→                currentStepIndex === totalSteps - 1
   773→                  ? 'bg-green-500 text-white hover:bg-green-600'
   774→                  : 'bg-[#00f3ff] text-black hover:shadow-[0_0_20px_rgba(0,243,255,0.4)]'
