    18→  TIER_DEFINITIONS,
    19→  MALE_HEIGHT_RATINGS,
    20→  FEMALE_HEIGHT_RATINGS,
    21→  BODY_RATINGS,
    22→  TierInfo,
    23→} from '@/types/psl';
    24→import { calculateFFMI, FFMIResult } from './ffmi-calculator';
    25→
