### 4.4 Calculated Landmark Functions

```javascript
/**
 * Calculate derived landmarks from raw detected points
 */
const CALCULATED_LANDMARKS = {
    vertex: (landmarks) => {
        const glabella = getLandmark(landmarks, 10);
        const occiput = getLandmark(landmarks, 8);
        const nasion = getLandmark(landmarks, 1);
        const hairline = getLandmark(landmarks, 12);
        const distance = euclidean(glabella, occiput);
        return translatePoint(glabella, {
            along: { from: nasion, to: hairline },
            by: -distance
        });
    },

    occiput: (landmarks) => {
        const vertex = getLandmark(landmarks, 9);
        const pronasale = getLandmark(landmarks, 83);
        const distance = euclidean(vertex, pronasale);
        return translatePoint(vertex, {
            along: { from: pronasale, to: vertex },
            by: distance
        });
    },

    menton: (landmarks) => {
        const chinFront = getLandmark(landmarks, 0);
        const chinBottom = getLandmark(landmarks, 8);
        return {
            x: (chinFront.x + chinBottom.x) / 2,
            y: (chinFront.y + chinBottom.y) / 2
        };
    },

    rhinion: (landmarks, namedPoints) => {
        if (!namedPoints.pronasale || !namedPoints.nasion) return null;
        const pronasale = toPixels(namedPoints.pronasale, landmarks.imageWidth, landmarks.imageHeight);
        return translatePoint(pronasale, {
            along: { from: pronasale, to: toPixels(namedPoints.nasion, landmarks.imageWidth, landmarks.imageHeight) },
            by: euclidean(getLandmark(landmarks, 74), getLandmark(landmarks, 72))
        });
    }
    // ... additional calculated points
};
