## 5. Ratio Calculation Functions

### 5.1 Core Utility Functions

```javascript
/**
 * Calculate Euclidean distance between two points
 */
function euclidean(point1, point2) {
    const dx = point2.x - point1.x;
    const dy = point2.y - point1.y;
    return Math.sqrt(dx * dx + dy * dy);
}

/**
 * Calculate midpoint between two points
 */
function midpoint(point1, point2) {
    return {
        x: (point1.x + point2.x) / 2,
        y: (point1.y + point2.y) / 2
    };
}

/**
 * Safe division with fallback
 */
function ratio(numerator, denominator, fallback = 0) {
    if (!isFinite(numerator) || !isFinite(denominator) || Math.abs(denominator) < 1e-10) {
        return fallback;
    }
    const result = numerator / denominator;
    return isFinite(result) ? result : fallback;
}

/**
 * Calculate angle at vertex (in degrees)
 * Returns angle ABC where B is the vertex
 */
function angle(pointA, vertex, pointC) {
    const vectorBA = { x: pointA.x - vertex.x, y: pointA.y - vertex.y };
    const vectorBC = { x: pointC.x - vertex.x, y: pointC.y - vertex.y };

    const dotProduct = vectorBA.x * vectorBC.x + vectorBA.y * vectorBC.y;
    const magnitudeProduct = Math.sqrt(vectorBA.x ** 2 + vectorBA.y ** 2) *
                             Math.sqrt(vectorBC.x ** 2 + vectorBC.y ** 2);

    if (Math.abs(magnitudeProduct) < 1e-10) return 0;

    const cosAngle = Math.max(-1, Math.min(1, dotProduct / magnitudeProduct));
    return (180 / Math.PI) * Math.acos(cosAngle);
}
```

### 5.2 Front Profile Ratio Calculations

```javascript
const FRONT_RATIO_CALCULATIONS = {
    "Midface Ratio": (landmarks) => {
        const leftPupil = landmarks.leftEyePupil;
        const rightPupil = landmarks.rightEyePupil;
        const cupidsBow = landmarks.cupidsBow;
        if (!leftPupil || !rightPupil || !cupidsBow) return null;

        const eyeWidth = Math.abs(rightPupil.x - leftPupil.x);
        const eyeCenterY = (leftPupil.y + rightPupil.y) / 2;
        return ratio(eyeWidth, Math.abs(cupidsBow.y - eyeCenterY));
    },

    "Eye Separation Ratio": (landmarks) => {
        const leftPupil = landmarks.leftEyePupil;
        const rightPupil = landmarks.rightEyePupil;
        const leftCheek = landmarks.leftCheek;
        const rightCheek = landmarks.rightCheek;
        if (!leftPupil || !rightPupil || !leftCheek || !rightCheek) return null;

        return 100 * ratio(
            Math.abs(rightPupil.x - leftPupil.x),
            Math.abs(rightCheek.x - leftCheek.x)
        );
    },

    "Total Facial Width to Height Ratio": (landmarks) => {
        const leftCheek = landmarks.leftCheek;
        const rightCheek = landmarks.rightCheek;
        const hairline = landmarks.hairline;
        const chinBottom = landmarks.chinBottom;
        if (!leftCheek || !rightCheek || !hairline || !chinBottom) return null;

        return ratio(
            Math.abs(chinBottom.y - hairline.y),
            Math.abs(rightCheek.x - leftCheek.x)
        );
    },

    "Bigonial Width": (landmarks) => {
        const leftGonion = landmarks.leftTopGonion;
        const rightGonion = landmarks.rightTopGonion;
        const leftCheek = landmarks.leftCheek;
        const rightCheek = landmarks.rightCheek;
        if (!leftGonion || !rightGonion || !leftCheek || !rightCheek) return null;

        return 100 * ratio(
            Math.abs(rightGonion.x - leftGonion.x),
            Math.abs(rightCheek.x - leftCheek.x)
        );
    },

    "Lateral Canthal Tilt": (landmarks) => {
        const leftMedial = landmarks.leftEyeMedialCanthus;
        const leftLateral = landmarks.leftEyeLateralCanthus;
        const rightMedial = landmarks.rightEyeMedialCanthus;
        const rightLateral = landmarks.rightEyeLateralCanthus;
        if (!leftMedial || !leftLateral || !rightMedial || !rightLateral) return null;

        const leftTilt = (180 / Math.PI) * Math.atan2(
            leftMedial.y - leftLateral.y,
            leftMedial.x - leftLateral.x
        );
        const rightTilt = (180 / Math.PI) * Math.atan2(
            rightMedial.y - rightLateral.y,
            rightLateral.x - rightMedial.x
        );
        return (leftTilt + rightTilt) / 2;
    },

    "Lower Third": (landmarks) => {
        const hairline = landmarks.hairline;
        const noseBottom = landmarks.noseBottom;
        const chinBottom = landmarks.chinBottom;
        const leftBrowInner = landmarks.leftBrowInnerCorner;
        const leftBrowHead = landmarks.leftBrowHead;
        const rightBrowInner = landmarks.rightBrowInnerCorner;
        const rightBrowHead = landmarks.rightBrowHead;
        if (!hairline || !noseBottom || !chinBottom || !leftBrowInner ||
            !leftBrowHead || !rightBrowInner || !rightBrowHead) return null;

        const faceHeight = Math.abs(chinBottom.y - hairline.y);
        return ((chinBottom.y - noseBottom.y) / faceHeight) * 100;
    },

    "Middle Third": (landmarks) => {
        const hairline = landmarks.hairline;
        const noseBottom = landmarks.noseBottom;
        const chinBottom = landmarks.chinBottom;
        const leftBrowInner = landmarks.leftBrowInnerCorner;
        const leftBrowHead = landmarks.leftBrowHead;
        const rightBrowInner = landmarks.rightBrowInnerCorner;
        const rightBrowHead = landmarks.rightBrowHead;
        if (!hairline || !noseBottom || !chinBottom || !leftBrowInner ||
            !leftBrowHead || !rightBrowInner || !rightBrowHead) return null;

        const faceHeight = Math.abs(chinBottom.y - hairline.y);
        const browMid = midpoint(midpoint(leftBrowInner, leftBrowHead),
                                  midpoint(rightBrowInner, rightBrowHead));
        return ((noseBottom.y - browMid.y) / faceHeight) * 100;
    },

    "Top Third": (landmarks) => {
        const hairline = landmarks.hairline;
        const chinBottom = landmarks.chinBottom;
        const leftBrowInner = landmarks.leftBrowInnerCorner;
        const leftBrowHead = landmarks.leftBrowHead;
        const rightBrowInner = landmarks.rightBrowInnerCorner;
        const rightBrowHead = landmarks.rightBrowHead;
        if (!hairline || !chinBottom || !leftBrowInner || !leftBrowHead ||
            !rightBrowInner || !rightBrowHead) return null;

        const faceHeight = Math.abs(chinBottom.y - hairline.y);
        const browMid = midpoint(midpoint(leftBrowInner, leftBrowHead),
                                  midpoint(rightBrowInner, rightBrowHead));
        return ((browMid.y - hairline.y) / faceHeight) * 100;
    },

    "Eye Aspect Ratio": (landmarks) => {
        const leftMedial = landmarks.leftEyeMedialCanthus;
        const leftLateral = landmarks.leftEyeLateralCanthus;
        const leftUpper = landmarks.leftEyeUpperEyelid;
        const leftLower = landmarks.leftEyeLowerEyelid;
        const rightMedial = landmarks.rightEyeMedialCanthus;
        const rightLateral = landmarks.rightEyeLateralCanthus;
        const rightUpper = landmarks.rightEyeUpperEyelid;
        const rightLower = landmarks.rightEyeLowerEyelid;
        if (!leftMedial || !leftLateral || !leftUpper || !leftLower ||
            !rightMedial || !rightLateral || !rightUpper || !rightLower) return null;

        const leftRatio = ratio(Math.abs(leftLateral.x - leftMedial.x),
                                Math.abs(leftLower.y - leftUpper.y));
        const rightRatio = ratio(Math.abs(rightLateral.x - rightMedial.x),
                                 Math.abs(rightLower.y - rightUpper.y));
        return (leftRatio + rightRatio) / 2;
    },

    "Jaw Frontal Angle": (landmarks) => {
        const leftGonion = landmarks.leftBottomGonion;
        const rightGonion = landmarks.rightBottomGonion;
        const chinLeft = landmarks.chinLeft;
        const chinRight = landmarks.chinRight;
        if (!leftGonion || !rightGonion || !chinLeft || !chinRight) return null;

        // Calculate angle between jaw lines
        return 180 - angle(leftGonion, chinLeft, chinRight, rightGonion);
    }
};
```

### 5.3 Side Profile Ratio Calculations

```javascript
const SIDE_RATIO_CALCULATIONS = {
    "Gonial Angle": (landmarks) => {
        const gonionTop = landmarks.gonionTop;
        const gonionBottom = landmarks.gonionBottom;
        const menton = landmarks.menton;
        const tragus = landmarks.intertragicNotch;
        if (!gonionTop || !gonionBottom || !menton || !tragus) return null;

        const angle1 = Math.atan2(gonionTop.y - tragus.y, gonionTop.x - tragus.x);
        const angle2 = Math.atan2(gonionBottom.y - menton.y, gonionBottom.x - menton.x);
        let angleDiff = (180 / Math.PI) * Math.abs(angle1 - angle2);
        return angleDiff > 180 ? 360 - angleDiff : angleDiff;
    },

    "Nasolabial Angle": (landmarks) => {
        const columella = landmarks.columella;
        const subnasale = landmarks.subnasale;
        const labraleSuperius = landmarks.labraleSuperius;
        if (!columella || !subnasale || !labraleSuperius) return null;

        return angle(columella, subnasale, labraleSuperius);
    },

    "Nasofrontal Angle": (landmarks) => {
        const glabella = landmarks.glabella;
        const nasion = landmarks.nasion;
        const supratip = landmarks.supratip;
        if (!glabella || !nasion || !supratip) return null;

        return angle(glabella, nasion, supratip);
    },

    "Mentolabial Angle": (landmarks) => {
        const labraleInferius = landmarks.labraleInferius;
        const sublabiale = landmarks.sublabiale;
        const pogonion = landmarks.pogonion;
        if (!labraleInferius || !sublabiale || !pogonion) return null;

        return angle(labraleInferius, sublabiale, pogonion);
    },

    "Facial Convexity (Glabella)": (landmarks) => {
        const glabella = landmarks.glabella;
        const subnasale = landmarks.subnasale;
        const pogonion = landmarks.pogonion;
        if (!glabella || !subnasale || !pogonion) return null;

        return angle(glabella, subnasale, pogonion);
    },

    "Total Facial Convexity": (landmarks) => {
        const glabella = landmarks.glabella;
        const pronasale = landmarks.pronasale;
        const pogonion = landmarks.pogonion;
        if (!glabella || !pronasale || !pogonion) return null;

        const baseAngle = angle(glabella, pronasale, pogonion);

        // Determine if convex or concave
        const v1 = { x: glabella.x - pronasale.x, y: glabella.y - pronasale.y };
        const v2 = { x: pogonion.x - pronasale.x, y: pogonion.y - pronasale.y };
        const cross = v1.x * v2.y - v1.y * v2.x;

        return cross < 0 ? baseAngle : 360 - baseAngle;
    },

    "Submental Cervical Angle": (landmarks) => {
        const menton = landmarks.menton;
        const cervicalPoint = landmarks.cervicalPoint;
        const neckPoint = landmarks.neckPoint;
        if (!menton || !cervicalPoint || !neckPoint) return null;

        return angle(menton, cervicalPoint, neckPoint);
    },

    "Nasal Tip Angle": (landmarks) => {
        const supratip = landmarks.supratip;
        const pronasale = landmarks.pronasale;
        const infratip = landmarks.infratip;
        if (!supratip || !pronasale || !infratip) return null;

        return angle(supratip, pronasale, infratip);
    },

    "Nasofacial Angle": (landmarks) => {
        const nasion = landmarks.nasion;
        const pronasale = landmarks.pronasale;
        const pogonion = landmarks.pogonion;
        if (!nasion || !pronasale || !pogonion) return null;

        const baseAngle = angle(pronasale, nasion, pogonion);
        return baseAngle > 90 ? 180 - baseAngle : baseAngle;
    },

