cat << 'EOF' | npx tsx -
import { 
  getScoringCurve, 
  getDisplayCurve, 
  hasDisplayCurve, 
  getDualCurves,
  BEZIER_CURVES,
  DISPLAY_CURVES
} from './src/lib/bezier-curves';

// Test 1: Verify DISPLAY_CURVES has 3 entries
console.log('Test 1: DISPLAY_CURVES count:', Object.keys(DISPLAY_CURVES).length === 3 ? 'PASS' : 'FAIL');

// Test 2: getScoringCurve returns scoring curve
const scoringCurve = getScoringCurve('faceWidthToHeight');
console.log('Test 2: getScoringCurve returns points:', scoringCurve && scoringCurve.length > 0 ? 'PASS' : 'FAIL');

// Test 3: getDisplayCurve returns display curve when available
const displayCurve = getDisplayCurve('faceWidthToHeight');
console.log('Test 3: getDisplayCurve returns display curve:', displayCurve && displayCurve !== scoringCurve ? 'PASS' : 'FAIL');

// Test 4: getDisplayCurve falls back to scoring curve when no display curve
const midfaceScoring = getScoringCurve('midfaceRatio');
