export function QualityBadge({ quality, size = 'md' }: QualityBadgeProps) {
  const color = getQualityColor(quality);
  const sizeClasses = {
    sm: 'px-1.5 py-0.5 text-[10px]',
    md: 'px-2 py-1 text-xs',
    lg: 'px-3 py-1.5 text-sm',
