'use client';

import { useMemo, useState } from 'react';
import { motion } from 'framer-motion';
import {
  Ruler,
  Scale,
  Info,
  TrendingUp,
  Calculator,
  AlertCircle,
  Dumbbell,
  Camera,
  Percent,
  BookOpen,
  Clock,
  ArrowRight,
