interface TreatmentTimelineProps {
  recommendations: Recommendation[];
  className?: string;
}

interface TimelinePhase {
  id: string;
  name: string;
  color: string;
  bgColor: string;
  borderColor: string;
  icon: 'foundation' | 'minimal' | 'surgical';
  order: number;
}

const PHASES: Record<string, TimelinePhase> = {
  Foundational: {
    id: 'foundational',
    name: 'Foundational',
    color: '#22c55e',
    bgColor: 'rgba(34, 197, 94, 0.1)',
    borderColor: 'rgba(34, 197, 94, 0.3)',
    icon: 'foundation',
    order: 1,
  },
  'Minimally Invasive': {
    id: 'minimal',
    name: 'Minimally Invasive',
    color: '#fbbf24',
    bgColor: 'rgba(251, 191, 36, 0.1)',
    borderColor: 'rgba(251, 191, 36, 0.3)',
    icon: 'minimal',
    order: 2,
  },
  Surgical: {
    id: 'surgical',
    name: 'Surgical',
    color: '#ef4444',
    bgColor: 'rgba(239, 68, 68, 0.1)',
    borderColor: 'rgba(239, 68, 68, 0.3)',
    icon: 'surgical',
    order: 3,
  },
};

interface GroupedRecommendations {
  phase: TimelinePhase;
  items: Recommendation[];
  totalWeeks: number;
}

function groupByPhase(recommendations: Recommendation[]): GroupedRecommendations[] {
  const groups = new Map<string, Recommendation[]>();

  recommendations.forEach((rec) => {
    const existing = groups.get(rec.phase) || [];
    existing.push(rec);
    groups.set(rec.phase, existing);
  });

