   250→    id: data.id,
   251→    name: data.name,
   252→    slug: data.slug,
   253→    description: data.description,
   254→    icon: data.icon,
   255→    displayOrder: data.display_order,
   256→    postCount: data.post_count,
   257→  };
   258→}
   259→
   260→function transformCategory(data: CategoryApiResponse): Category {
