    secondary: {
      category: string;
      sub_archetype: string;
      confidence: number;
      traits: string[];
    } | null;
    all_scores: Array<{ category: string; score: number; confidence: number }>;
    dimorphism_level: string;
    style_guide: {
      clothing: string[];
      hair: string[];
      colors: string[];
