'use client';

import { useEffect, useState, useCallback, Suspense } from 'react';
import { useSearchParams } from 'next/navigation';
import Link from 'next/link';
import { motion } from 'framer-motion';
import {
  Search,
  MessageSquare,
  FileText,
  ThumbsUp,
  Clock,
  ArrowRight,
  User,
  ChevronLeft,
  ChevronRight,
  AlertCircle,
} from 'lucide-react';
import { ForumHeader, ForumBreadcrumb, SearchBar } from '@/components/forum';
import { api } from '@/lib/api';
import type { ForumSearchResult, ForumSearchResponse } from '@/types/forum';

// ============================================
// SEARCH RESULT CARD
// ============================================
function SearchResultCard({ result, index }: { result: ForumSearchResult; index: number }) {
  const isPost = result.type === 'post';
  const linkUrl = isPost
    ? `/forum/post/${result.id}`
    : `/forum/post/${result.postId}#comment-${result.id}`;

  // Format date
  const date = new Date(result.createdAt);
  const timeAgo = getTimeAgo(date);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: index * 0.05 }}
    >
      <Link href={linkUrl}>
        <div className="group p-6 rounded-2xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all">
          <div className="flex items-start gap-4">
            {/* Type Icon */}
            <div className={`w-10 h-10 rounded-xl flex items-center justify-center flex-shrink-0 ${
              isPost
                ? 'bg-gradient-to-br from-cyan-500/20 to-blue-500/20 border border-cyan-500/30'
                : 'bg-gradient-to-br from-purple-500/20 to-pink-500/20 border border-purple-500/30'
            }`}>
              {isPost ? (
                <FileText size={16} className="text-cyan-400" />
              ) : (
                <MessageSquare size={16} className="text-purple-400" />
              )}
            </div>

            {/* Content */}
            <div className="flex-1 min-w-0">
              {/* Type badge + title/post reference */}
              <div className="flex flex-wrap items-center gap-2 mb-2">
                <span className={`px-2 py-0.5 rounded-md text-[9px] font-black uppercase tracking-widest ${
                  isPost
                    ? 'bg-cyan-500/10 text-cyan-400 border border-cyan-500/20'
                    : 'bg-purple-500/10 text-purple-400 border border-purple-500/20'
                }`}>
                  {isPost ? 'Post' : 'Comment'}
                </span>

                {isPost && result.title && (
                  <h3 className="text-sm font-bold text-white group-hover:text-cyan-400 transition-colors truncate">
                    {result.title}
                  </h3>
                )}

                {!isPost && result.postTitle && (
                  <span className="text-xs text-neutral-500 truncate">
                    on &quot;{result.postTitle}&quot;
                  </span>
                )}
              </div>

              {/* Content preview */}
              <p className="text-[12px] text-neutral-400 line-clamp-2 leading-relaxed mb-3">
                {result.contentPreview}
              </p>

              {/* Meta info */}
              <div className="flex flex-wrap items-center gap-4 text-[10px] text-neutral-500">
                <span className="flex items-center gap-1.5">
                  <User size={12} />
                  {result.author.username}
                </span>
                <span className="flex items-center gap-1.5">
                  <ThumbsUp size={12} />
                  {result.voteCount} votes
                </span>
                <span className="flex items-center gap-1.5">
                  <Clock size={12} />
                  {timeAgo}
                </span>
                {result.categorySlug && (
                  <span className="px-2 py-0.5 rounded-md bg-neutral-800 text-neutral-400 uppercase tracking-wider font-bold">
                    {result.categorySlug.replace(/-/g, ' ')}
                  </span>
                )}
              </div>
            </div>

            {/* Arrow */}
            <div className="flex-shrink-0 w-8 h-8 rounded-lg bg-neutral-800/50 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
              <ArrowRight size={14} className="text-cyan-400" />
            </div>
          </div>
        </div>
      </Link>
    </motion.div>
  );
}

