// ============================================
// PAGINATION
// ============================================
function Pagination({
  currentPage,
  totalPages,
  onPageChange,
}: {
  currentPage: number;
  totalPages: number;
  onPageChange: (page: number) => void;
}) {
  if (totalPages <= 1) return null;

  return (
    <div className="flex items-center justify-center gap-2 mt-8">
      <button
        onClick={() => onPageChange(currentPage - 1)}
        disabled={currentPage === 1}
        className="h-10 px-4 rounded-xl bg-neutral-900/50 border border-white/10 text-neutral-400
                   hover:text-white hover:border-white/20 disabled:opacity-50 disabled:cursor-not-allowed
                   flex items-center gap-2 text-[10px] font-black uppercase tracking-widest transition-all"
      >
        <ChevronLeft size={14} />
        Prev
      </button>

      <span className="px-4 text-sm text-neutral-500">
        Page <span className="text-white font-bold">{currentPage}</span> of{' '}
        <span className="text-white font-bold">{totalPages}</span>
      </span>

      <button
        onClick={() => onPageChange(currentPage + 1)}
        disabled={currentPage === totalPages}
        className="h-10 px-4 rounded-xl bg-neutral-900/50 border border-white/10 text-neutral-400
                   hover:text-white hover:border-white/20 disabled:opacity-50 disabled:cursor-not-allowed
                   flex items-center gap-2 text-[10px] font-black uppercase tracking-widest transition-all"
      >
        Next
        <ChevronRight size={14} />
      </button>
    </div>
  );
}

