     1→'use client';
     2→
     3→import { useState } from 'react';
     4→import { motion, AnimatePresence } from 'framer-motion';
     5→import {
     6→  BookOpen,
     7→  Dumbbell,
     8→  Sparkles,
     9→  Clock,
    10→  ChevronRight,
    11→  Search,
    12→  Brain,
    13→  Wrench,
    14→  TrendingDown,
    15→  Calendar,
    16→  Target,
    17→  Heart,
    18→  Utensils,
    19→  Droplet,
    20→  X,
    21→  ArrowLeft,
    22→  MessageSquare,
    23→  ExternalLink,
    24→  ShoppingCart,
    25→} from 'lucide-react';
    26→import Image from 'next/image';
    27→import Link from 'next/link';
    28→import { TabContent } from '../ResultsLayout';
    29→import {
    30→  GUIDE_CATEGORIES,
    31→  getGuidesByCategory,
    32→  getTotalGuideCount,
    33→  getTotalReadTime,
    34→  searchGuides,
    35→  getGuideProductById,
    36→} from '@/data/guides';
    37→import { Guide, GuideCategory, GuideMedia } from '@/types/guides';
    38→import { useRegion } from '@/contexts/RegionContext';
    39→import ReactMarkdown from 'react-markdown';
    40→
    41→// ============================================
    42→// ICON MAPPING
    43→// ============================================
    44→
    45→const ICON_MAP: Record<string, React.ReactNode> = {
    46→  BookOpen: <BookOpen size={20} />,
    47→  Dumbbell: <Dumbbell size={20} />,
    48→  Sparkles: <Sparkles size={20} />,
    49→  Brain: <Brain size={20} />,
    50→  Wrench: <Wrench size={20} />,
    51→  TrendingDown: <TrendingDown size={20} />,
    52→  Calendar: <Calendar size={20} />,
    53→  Target: <Target size={20} />,
    54→  Heart: <Heart size={20} />,
    55→  Utensils: <Utensils size={20} />,
    56→  Droplet: <Droplet size={20} />,
    57→};
    58→
    59→function getIconComponent(iconName: string, size = 20): React.ReactNode {
    60→  // Check icon map first
    61→  const mappedIcon = ICON_MAP[iconName];
    62→  if (mappedIcon) return mappedIcon;
    63→
    64→  // Fallback to BookOpen
    65→  return <BookOpen size={size} />;
    66→}
    67→
    68→// ============================================
    69→// MEDIA RENDERER
    70→// ============================================
    71→
    72→interface MediaRendererProps {
    73→  media: GuideMedia;
    74→}
    75→
    76→function MediaRenderer({ media }: MediaRendererProps) {
    77→  const placementClasses = {
    78→    inline: 'max-w-md mx-auto',
    79→    hero: 'w-full max-w-2xl mx-auto',
    80→    'full-width': 'w-full',
    81→  };
    82→
    83→  const isGif = media.type === 'gif' || media.url.endsWith('.gif');
    84→
    85→  return (
    86→    <figure className={`my-6 ${placementClasses[media.placement || 'inline']}`}>
    87→      <div className="relative rounded-lg overflow-hidden bg-neutral-800">
    88→        {isGif ? (
    89→          // Use img for GIFs to preserve animation
    90→          // eslint-disable-next-line @next/next/no-img-element
    91→          <img
    92→            src={media.url}
    93→            alt={media.alt}
    94→            className="w-full h-auto"
    95→            loading="lazy"
    96→          />
    97→        ) : (
    98→          <Image
    99→            src={media.url}
   100→            alt={media.alt}
   101→            width={media.width || 600}
   102→            height={media.height || 400}
   103→            className="w-full h-auto"
   104→            placeholder="blur"
   105→            blurDataURL="data:image/jpeg;base64,"
   106→          />
   107→        )}
   108→      </div>
   109→      {media.caption && (
   110→        <figcaption className="mt-2 text-center text-sm text-neutral-400 italic">
   111→          {media.caption}
   112→        </figcaption>
   113→      )}
   114→    </figure>
   115→  );
   116→}
   117→
   118→// ============================================
   119→// PRODUCT CALLOUT (CONVERSION OPTIMIZED)
   120→// ============================================
   121→
   122→interface ProductCalloutProps {
   123→  productId: string;
   124→}
   125→
   126→function ProductCallout({ productId }: ProductCalloutProps) {
   127→  const { getLink } = useRegion();
   128→  const product = getGuideProductById(productId);
   129→
   130→  if (!product) return null;
   131→
   132→  const link = getLink(product.regionLinks, product.directLink);
   133→
   134→  return (
   135→    <div className="my-6 p-4 bg-gradient-to-r from-cyan-500/10 to-blue-500/10 border border-cyan-500/20 rounded-xl">
   136→      <div className="flex items-center gap-4">
   137→        {/* Product Image */}
   138→        {product.imageUrl && (
   139→          <div className="w-16 h-16 bg-neutral-800 rounded-lg overflow-hidden flex-shrink-0">
   140→            <Image
   141→              src={product.imageUrl}
   142→              alt={product.name}
   143→              width={64}
   144→              height={64}
   145→              className="w-full h-full object-cover"
   146→            />
   147→          </div>
   148→        )}
   149→
   150→        {/* Product Info */}
   151→        <div className="flex-1 min-w-0">
   152→          <div className="flex items-center gap-2">
   153→            <h4 className="font-semibold text-white truncate">{product.name}</h4>
   154→            {product.brand && (
   155→              <span className="text-xs text-neutral-500">{product.brand}</span>
   156→            )}
   157→          </div>
   158→          <p className="text-sm text-cyan-300 italic">&ldquo;{product.tagline}&rdquo;</p>
   159→        </div>
   160→
   161→        {/* CTA Button */}
   162→        <a
   163→          href={link}
   164→          target="_blank"
   165→          rel="noopener noreferrer"
   166→          className="flex-shrink-0 flex items-center gap-2 px-4 py-2 bg-cyan-500 hover:bg-cyan-400 text-black font-medium rounded-lg transition-colors"
   167→        >
   168→          <ShoppingCart size={16} />
   169→          <span className="hidden sm:inline">Buy Now</span>
   170→        </a>
   171→      </div>
   172→    </div>
   173→  );
   174→}
   175→
   176→// ============================================
   177→// FORUM DISCUSSION LINK
   178→// ============================================
   179→
   180→interface ForumDiscussionLinkProps {
   181→  forumCategory?: string;
   182→  guideTitle: string;
   183→}
   184→
   185→function ForumDiscussionLink({ forumCategory, guideTitle }: ForumDiscussionLinkProps) {
   186→  if (!forumCategory) return null;
   187→
   188→  return (
   189→    <div className="mt-8 p-6 bg-neutral-900/50 border border-neutral-800 rounded-xl">
   190→      <div className="flex items-center gap-3 mb-3">
   191→        <div className="w-10 h-10 bg-purple-500/10 rounded-xl flex items-center justify-center">
   192→          <MessageSquare size={20} className="text-purple-400" />
   193→        </div>
   194→        <div>
   195→          <h3 className="font-semibold text-white">Discuss This Guide</h3>
   196→          <p className="text-sm text-neutral-400">Join the community conversation</p>
   197→        </div>
   198→      </div>
   199→
   200→      <p className="text-sm text-neutral-400 mb-4">
   201→        Have questions about {guideTitle.toLowerCase()}? Want to share your experience?
   202→        Join the discussion in our community forum.
   203→      </p>
   204→
   205→      <Link
   206→        href={`/forum/${forumCategory}`}
   207→        className="inline-flex items-center gap-2 px-4 py-2 bg-purple-500/10 hover:bg-purple-500/20 text-purple-400 border border-purple-500/30 rounded-lg transition-colors"
   208→      >
   209→        <MessageSquare size={16} />
   210→        Go to Forum
   211→        <ExternalLink size={14} />
   212→      </Link>
   213→    </div>
   214→  );
   215→}
   216→
   217→// ============================================
   218→// GUIDE STATS CARD
   219→// ============================================
   220→
   221→function GuideStatsCard() {
   222→  const totalGuides = getTotalGuideCount();
   223→  const totalMinutes = getTotalReadTime();
   224→
   225→  return (
   226→    <motion.div
   227→      className="bg-gradient-to-br from-neutral-900 to-neutral-950 border border-neutral-800 rounded-2xl p-6 mb-6"
   228→      initial={{ opacity: 0, y: 20 }}
   229→      animate={{ opacity: 1, y: 0 }}
   230→    >
   231→      <div className="flex items-center gap-3 mb-4">
   232→        <div className="w-10 h-10 bg-cyan-500/10 rounded-xl flex items-center justify-center">
   233→          <BookOpen size={20} className="text-cyan-400" />
   234→        </div>
   235→        <div>
   236→          <h2 className="font-semibold text-white">Product Guides</h2>
   237→          <p className="text-sm text-neutral-400">Evidence-based looksmaxxing resources</p>
   238→        </div>
   239→      </div>
   240→
   241→      <div className="grid grid-cols-2 gap-4">
   242→        <div className="bg-neutral-900/50 rounded-xl p-4">
   243→          <div className="text-2xl font-bold text-white mb-1">{totalGuides}</div>
   244→          <div className="text-xs text-neutral-500">Total Guides</div>
   245→        </div>
   246→        <div className="bg-neutral-900/50 rounded-xl p-4">
   247→          <div className="flex items-center gap-1.5 mb-1">
   248→            <Clock size={16} className="text-cyan-400" />
   249→            <div className="text-2xl font-bold text-white">{totalMinutes}</div>
   250→          </div>
   251→          <div className="text-xs text-neutral-500">Minutes of Content</div>
   252→        </div>
   253→      </div>
   254→    </motion.div>
   255→  );
   256→}
   257→
   258→// ============================================
   259→// GUIDE VIEWER MODAL
   260→// ============================================
   261→
   262→interface GuideViewerProps {
   263→  guide: Guide;
   264→  onClose: () => void;
   265→}
   266→
   267→function GuideViewer({ guide, onClose }: GuideViewerProps) {
   268→  const [currentSection, setCurrentSection] = useState(0);
   269→
   270→  const section = guide.sections[currentSection];
   271→
   272→  return (
   273→    <motion.div
   274→      initial={{ opacity: 0 }}
   275→      animate={{ opacity: 1 }}
   276→      exit={{ opacity: 0 }}
   277→      className="fixed inset-0 z-50 bg-black/90 backdrop-blur-sm overflow-y-auto"
   278→    >
   279→      <div className="min-h-screen">
   280→        {/* Header */}
   281→        <div className="sticky top-0 z-10 bg-neutral-950/95 backdrop-blur border-b border-neutral-800">
   282→          <div className="max-w-4xl mx-auto px-4 py-4 flex items-center justify-between">
   283→            <button
   284→              onClick={onClose}
   285→              className="flex items-center gap-2 text-neutral-400 hover:text-white transition-colors"
   286→            >
   287→              <ArrowLeft size={20} />
   288→              <span className="hidden sm:inline">Back to Guides</span>
   289→            </button>
   290→            <div className="flex items-center gap-2 text-neutral-500 text-sm">
   291→              <Clock size={14} />
   292→              <span>{guide.estimatedReadTime} min read</span>
   293→            </div>
   294→            <button
   295→              onClick={onClose}
   296→              className="p-2 hover:bg-neutral-800 rounded-lg transition-colors"
   297→            >
   298→              <X size={20} className="text-neutral-400" />
   299→            </button>
   300→          </div>
   301→        </div>
   302→
   303→        {/* Content */}
   304→        <div className="max-w-4xl mx-auto px-4 py-8">
   305→          {/* Hero Media */}
   306→          {guide.heroMedia && (
   307→            <MediaRenderer media={guide.heroMedia} />
   308→          )}
   309→
   310→          {/* Guide Header */}
   311→          <div className="mb-8">
   312→            <div className="flex items-center gap-3 mb-4">
   313→              <div className="w-12 h-12 bg-cyan-500/10 rounded-xl flex items-center justify-center text-cyan-400">
   314→                {getIconComponent(guide.icon, 24)}
   315→              </div>
   316→              <div>
   317→                <h1 className="text-2xl md:text-3xl font-bold text-white">{guide.title}</h1>
   318→                {guide.subtitle && (
   319→                  <p className="text-neutral-400 italic">{guide.subtitle}</p>
   320→                )}
   321→              </div>
   322→            </div>
   323→            <p className="text-neutral-300">{guide.description}</p>
   324→          </div>
   325→
   326→          {/* Section Navigation */}
   327→          <div className="flex gap-2 overflow-x-auto pb-4 mb-6 scrollbar-hide">
   328→            {guide.sections.map((s, idx) => (
   329→              <button
   330→                key={s.id}
   331→                onClick={() => setCurrentSection(idx)}
   332→                className={`px-4 py-2 rounded-lg whitespace-nowrap transition-all ${
   333→                  currentSection === idx
   334→                    ? 'bg-cyan-500 text-black font-medium'
   335→                    : 'bg-neutral-800 text-neutral-400 hover:bg-neutral-700 hover:text-white'
   336→                }`}
   337→              >
   338→                {s.title}
   339→              </button>
   340→            ))}
   341→          </div>
   342→
   343→          {/* Current Section Content */}
   344→          <AnimatePresence mode="wait">
   345→            <motion.div
   346→              key={section.id}
   347→              initial={{ opacity: 0, x: 20 }}
   348→              animate={{ opacity: 1, x: 0 }}
   349→              exit={{ opacity: 0, x: -20 }}
   350→              transition={{ duration: 0.2 }}
   351→              className="bg-neutral-900 rounded-xl p-6 border border-neutral-800"
   352→            >
   353→              <h2 className="text-xl font-semibold text-white mb-4">{section.title}</h2>
   354→
   355→              {/* Tips */}
   356→              {section.tips && section.tips.length > 0 && (
   357→                <div className="mb-6 p-4 bg-cyan-500/10 border border-cyan-500/20 rounded-lg">
   358→                  <p className="text-sm font-medium text-cyan-400 mb-2">Quick Tips:</p>
   359→                  <ul className="space-y-1">
   360→                    {section.tips.map((tip, i) => (
   361→                      <li key={i} className="text-sm text-cyan-100 flex items-start gap-2">
   362→                        <span className="text-cyan-400 mt-1">-</span>
   363→                        {tip}
   364→                      </li>
   365→                    ))}
   366→                  </ul>
   367→                </div>
   368→              )}
   369→
   370→              {/* Warnings */}
   371→              {section.warnings && section.warnings.length > 0 && (
   372→                <div className="mb-6 p-4 bg-amber-500/10 border border-amber-500/20 rounded-lg">
   373→                  <p className="text-sm font-medium text-amber-400 mb-2">Important:</p>
   374→                  <ul className="space-y-1">
   375→                    {section.warnings.map((warning, i) => (
   376→                      <li key={i} className="text-sm text-amber-100 flex items-start gap-2">
   377→                        <span className="text-amber-400 mt-1">!</span>
   378→                        {warning}
   379→                      </li>
   380→                    ))}
   381→                  </ul>
   382→                </div>
   383→              )}
   384→
   385→              {/* Media (Before Content) */}
   386→              {section.media && section.media.filter(m => m.placement === 'hero').map(media => (
   387→                <MediaRenderer key={media.id} media={media} />
   388→              ))}
   389→
   390→              {/* Markdown Content */}
   391→              <div className="prose prose-invert prose-sm max-w-none guide-content">
   392→                <ReactMarkdown>
   393→                  {section.content}
   394→                </ReactMarkdown>
   395→              </div>
   396→
   397→              {/* Media (Inline after content) */}
   398→              {section.media && section.media.filter(m => m.placement !== 'hero').map(media => (
   399→                <MediaRenderer key={media.id} media={media} />
   400→              ))}
   401→
   402→              {/* Product Callouts */}
   403→              {section.products && section.products.length > 0 && (
   404→                <div className="mt-6">
   405→                  <p className="text-sm font-medium text-neutral-400 mb-3">Recommended Products:</p>
   406→                  {section.products.map(productId => (
   407→                    <ProductCallout key={productId} productId={productId} />
   408→                  ))}
   409→                </div>
   410→              )}
   411→
   412→              {/* Section Navigation */}
   413→              <div className="flex items-center justify-between mt-8 pt-6 border-t border-neutral-800">
   414→                <button
   415→                  onClick={() => setCurrentSection(Math.max(0, currentSection - 1))}
   416→                  disabled={currentSection === 0}
   417→                  className={`flex items-center gap-2 px-4 py-2 rounded-lg transition-colors ${
   418→                    currentSection === 0
   419→                      ? 'text-neutral-600 cursor-not-allowed'
   420→                      : 'text-neutral-400 hover:text-white hover:bg-neutral-800'
   421→                  }`}
   422→                >
   423→                  <ArrowLeft size={16} />
   424→                  Previous
   425→                </button>
   426→                <span className="text-sm text-neutral-500">
   427→                  {currentSection + 1} / {guide.sections.length}
   428→                </span>
   429→                <button
   430→                  onClick={() => setCurrentSection(Math.min(guide.sections.length - 1, currentSection + 1))}
   431→                  disabled={currentSection === guide.sections.length - 1}
   432→                  className={`flex items-center gap-2 px-4 py-2 rounded-lg transition-colors ${
   433→                    currentSection === guide.sections.length - 1
   434→                      ? 'text-neutral-600 cursor-not-allowed'
   435→                      : 'text-cyan-400 hover:text-cyan-300 hover:bg-cyan-500/10'
   436→                  }`}
   437→                >
   438→                  Next
   439→                  <ChevronRight size={16} />
   440→                </button>
   441→              </div>
   442→            </motion.div>
   443→          </AnimatePresence>
   444→
   445→          {/* Forum Discussion Link (after last section) */}
   446→          {currentSection === guide.sections.length - 1 && (
   447→            <ForumDiscussionLink
   448→              forumCategory={guide.forumCategory}
   449→              guideTitle={guide.title}
   450→            />
   451→          )}
   452→        </div>
   453→      </div>
   454→    </motion.div>
   455→  );
   456→}
   457→
   458→// ============================================
   459→// GUIDE CARD
   460→// ============================================
   461→
   462→interface GuideCardProps {
   463→  guide: Guide;
   464→  index: number;
   465→  onOpen: (guide: Guide) => void;
   466→}
   467→
   468→function GuideCard({ guide, index, onOpen }: GuideCardProps) {
   469→  const getCategoryColor = (guideId: string) => {
   470→    const category = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guideId));
   471→    if (!category) return 'cyan';
   472→    const colorMap: Record<string, string> = {
   473→      blue: 'cyan',
   474→      purple: 'purple',
   475→      amber: 'amber',
   476→    };
   477→    return colorMap[category.color] || 'cyan';
   478→  };
   479→
   480→  const color = getCategoryColor(guide.id);
   481→
   482→  const colorClasses = {
   483→    cyan: 'from-cyan-500/10 to-cyan-600/10 border-cyan-500/20 text-cyan-400',
   484→    purple: 'from-purple-500/10 to-purple-600/10 border-purple-500/20 text-purple-400',
   485→    amber: 'from-amber-500/10 to-amber-600/10 border-amber-500/20 text-amber-400',
   486→  };
   487→
   488→  const iconColorClasses = {
   489→    cyan: 'bg-cyan-500/10 text-cyan-400',
   490→    purple: 'bg-purple-500/10 text-purple-400',
   491→    amber: 'bg-amber-500/10 text-amber-400',
   492→  };
   493→
   494→  return (
   495→    <motion.div
   496→      onClick={() => onOpen(guide)}
   497→      className={`bg-gradient-to-br ${colorClasses[color as keyof typeof colorClasses]} border rounded-xl p-5 hover:border-opacity-50 transition-all cursor-pointer group`}
   498→      initial={{ opacity: 0, y: 20 }}
   499→      animate={{ opacity: 1, y: 0 }}
   500→      transition={{ delay: index * 0.05 }}
   501→      whileHover={{ y: -2 }}
   502→    >
   503→      <div className="flex items-start justify-between mb-3">
   504→        <div className={`w-10 h-10 ${iconColorClasses[color as keyof typeof iconColorClasses]} rounded-xl flex items-center justify-center`}>
   505→          {getIconComponent(guide.icon)}
   506→        </div>
   507→        <div className="flex items-center gap-1 text-neutral-500 text-xs">
   508→          <Clock size={12} />
   509→          <span>{guide.estimatedReadTime} min</span>
   510→        </div>
   511→      </div>
   512→
   513→      <h3 className="font-semibold text-white mb-1 group-hover:text-cyan-300 transition-colors">
   514→        {guide.title}
   515→      </h3>
   516→
   517→      {guide.subtitle && (
   518→        <p className="text-xs text-neutral-400 mb-2 italic">{guide.subtitle}</p>
   519→      )}
   520→
   521→      <p className="text-sm text-neutral-400 mb-3 line-clamp-2">{guide.description}</p>
   522→
   523→      <div className="flex items-center justify-between">
   524→        <div className="flex items-center gap-1 text-xs text-neutral-500">
   525→          <span>{guide.sections.length} sections</span>
   526→          {guide.productIds && guide.productIds.length > 0 && (
   527→            <>
   528→              <span>-</span>
   529→              <span>{guide.productIds.length} products</span>
   530→            </>
   531→          )}
   532→        </div>
   533→        <ChevronRight size={16} className="text-neutral-600 group-hover:text-cyan-400 transition-colors" />
   534→      </div>
   535→    </motion.div>
   536→  );
   537→}
   538→
   539→// ============================================
   540→// CATEGORY SECTION
   541→// ============================================
   542→
   543→interface CategorySectionProps {
   544→  category: GuideCategory;
   545→  onOpenGuide: (guide: Guide) => void;
   546→}
   547→
   548→function CategorySection({ category, onOpenGuide }: CategorySectionProps) {
   549→  const guides = getGuidesByCategory(category.id);
   550→
   551→  if (guides.length === 0) return null;
   552→
   553→  const colorClasses = {
   554→    blue: 'text-cyan-400 bg-cyan-500/10',
   555→    purple: 'text-purple-400 bg-purple-500/10',
   556→    amber: 'text-amber-400 bg-amber-500/10',
   557→  };
   558→
   559→  return (
   560→    <div className="mb-8">
   561→      <div className="flex items-center gap-3 mb-4">
   562→        <div className={`w-8 h-8 ${colorClasses[category.color as keyof typeof colorClasses]} rounded-lg flex items-center justify-center`}>
   563→          {getIconComponent(category.icon)}
   564→        </div>
   565→        <div>
   566→          <h3 className="font-semibold text-white">{category.name}</h3>
   567→          <p className="text-xs text-neutral-500">{category.description}</p>
   568→        </div>
   569→      </div>
   570→
   571→      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
   572→        {guides.map((guide, idx) => (
   573→          <GuideCard key={guide.id} guide={guide} index={idx} onOpen={onOpenGuide} />
   574→        ))}
   575→      </div>
   576→    </div>
   577→  );
   578→}
   579→
