  tags?: string[];
  order: number;  // Display order
  heroMedia?: GuideMedia;  // Hero image/GIF for the guide
  forumCategory?: string;  // Forum category slug for "Discuss in Forum"
}

// ============================================
// GUIDE CATEGORY TYPES
// ============================================

export interface GuideCategory {
  id: string;
  name: string;
  description: string;
  icon: string;
  color: string;
  guideIds: string[];
}

// ============================================
// CONTEXT STATE TYPES
// ============================================

export interface RegionState {
  region: Region;
  isAutoDetected: boolean;
  isLoading: boolean;
}

// ============================================
// LOCALE MAPPINGS
// ============================================

export const LOCALE_TO_REGION: Record<string, Region> = {
  'en-US': 'us',
  'en-GB': 'uk',
  'en-AU': 'au',
  'de-DE': 'de',
  'de-AT': 'de',
  'de-CH': 'de',
  'fr-FR': 'fr',
  'fr-CA': 'us',  // Ships from US
  'fr-BE': 'fr',
  'ja-JP': 'asia',
  'ko-KR': 'asia',
  'zh-CN': 'asia',
  'zh-TW': 'asia',
  'zh-HK': 'asia',
};

export const TIMEZONE_TO_REGION: Record<string, Region> = {
  // Americas
  'America/New_York': 'us',
  'America/Los_Angeles': 'us',
  'America/Chicago': 'us',
  'America/Denver': 'us',
  'America/Toronto': 'us',
  // Europe
  'Europe/London': 'uk',
  'Europe/Berlin': 'de',
  'Europe/Paris': 'fr',
  'Europe/Vienna': 'de',
  'Europe/Zurich': 'de',
  // Asia Pacific
  'Australia/Sydney': 'au',
  'Australia/Melbourne': 'au',
  'Australia/Brisbane': 'au',
  'Asia/Tokyo': 'asia',
  'Asia/Seoul': 'asia',
  'Asia/Shanghai': 'asia',
  'Asia/Hong_Kong': 'asia',
  'Asia/Singapore': 'asia',
};

// ============================================
// HELPER TYPES
// ============================================

export interface ProductClick {
  productId: string;
  region: Region;
  guideId?: string;
  sectionId?: string;
  timestamp: number;
}

export interface GuideView {
  guideId: string;
  region: Region;
  scrollDepth: number;  // 0-100
  timeSpentSeconds: number;
  timestamp: number;
}

