   126→          <div className="grid gap-4 md:grid-cols-2">
   127→            {[1, 2, 3, 4].map((i) => (
   128→              <div key={i} className="bg-neutral-900 border border-neutral-800 rounded-xl p-5 animate-pulse">
   129→                <div className="flex items-start gap-4">
   130→                  <div className="w-12 h-12 rounded-xl bg-neutral-800" />
   131→                  <div className="flex-1">
