  1097→/**
  1098→ * Get the appropriate metric configuration for a given gender and ethnicity
  1099→ * Falls back to base config if no override exists
  1100→ */
  1101→export function getMetricConfig(
  1102→  metricId: string,
  1103→  gender: Gender = 'male',
  1104→  ethnicity: Ethnicity = 'white'
  1105→): MasterMetricConfig | undefined {
  1106→  const key = `${gender}_${ethnicity}`;
  1107→  const override = ETHNICITY_OVERRIDES[key]?.[metricId];
  1108→  const baseConfig = MASTER_SCORING_DB[metricId];
  1109→
  1110→  if (!baseConfig) return undefined;
  1111→
  1112→  // Merge override with base config
  1113→  if (override) {
  1114→    return {
  1115→      ...baseConfig,
  1116→      ...override,
  1117→      flaws: {
  1118→        ...baseConfig.flaws,
  1119→        ...override.flaws
  1120→      }
  1121→    } as MasterMetricConfig;
  1122→  }
  1123→
  1124→  return baseConfig;
  1125→}
  1126→
  1127→// ============================================
  1128→// Z-SCORE SEVERITY FUNCTIONS
  1129→// ============================================
  1130→
  1131→/**
  1132→ * Calculate Z-score for a given value
  1133→ * Z = (value - mean) / std_dev
  1134→ */
  1135→export function calculateZScore(value: number, mean: number, stdDev: number): number {
  1136→  if (stdDev === 0) return 0;
  1137→  return Math.abs((value - mean) / stdDev);
  1138→}
  1139→
  1140→/**
  1141→ * Determine severity level based on Z-score and ideal range
  1142→ * - Ideal: Value is within ideal range
  1143→ * - Good: |z| < 1 (within 1 standard deviation)
  1144→ * - Moderate: 1 ≤ |z| < 2 (between 1-2 standard deviations)
  1145→ * - Severe: |z| ≥ 2 (more than 2 standard deviations away)
  1146→ */
  1147→export function getSeverityFromZScore(
  1148→  value: number,
  1149→  config: MasterMetricConfig
  1150→): { severity: SeverityLevel; zScore: number; isInIdeal: boolean } {
  1151→  const { ideal, mean, std_dev } = config;
  1152→  const isInIdeal = value >= ideal[0] && value <= ideal[1];
  1153→  const zScore = calculateZScore(value, mean, std_dev);
  1154→
  1155→  let severity: SeverityLevel;
  1156→  if (isInIdeal) {
  1157→    severity = 'ideal';
  1158→  } else if (zScore < 1) {
  1159→    severity = 'good';
  1160→  } else if (zScore < 2) {
  1161→    severity = 'moderate';
  1162→  } else {
  1163→    severity = 'severe';
  1164→  }
  1165→
  1166→  return { severity, zScore, isInIdeal };
  1167→}
  1168→
  1169→/**
  1170→ * Get severity for a metric value with ethnicity/gender awareness
  1171→ */
  1172→export function getSeverityForMetric(
  1173→  metricId: string,
  1174→  value: number,
  1175→  gender: Gender = 'male',
  1176→  ethnicity: Ethnicity = 'white'
  1177→): { severity: SeverityLevel; zScore: number; isInIdeal: boolean; config: MasterMetricConfig } | null {
  1178→  const config = getMetricConfig(metricId, gender, ethnicity);
  1179→  if (!config) return null;
  1180→
  1181→  const result = getSeverityFromZScore(value, config);
  1182→  return { ...result, config };
  1183→}
  1184→
  1185→/**
  1186→ * Get badge color for severity level
  1187→ */
  1188→export function getSeverityColor(severity: SeverityLevel): string {
  1189→  switch (severity) {
  1190→    case 'ideal': return 'green';
  1191→    case 'good': return 'blue';
  1192→    case 'moderate': return 'yellow';
  1193→    case 'severe': return 'red';
  1194→    default: return 'gray';
  1195→  }
  1196→}
  1197→
  1198→/**
  1199→ * Get severity label for display
  1200→ */
  1201→export function getSeverityLabel(severity: SeverityLevel): string {
  1202→  switch (severity) {
  1203→    case 'ideal': return 'Ideal';
  1204→    case 'good': return 'Good';
  1205→    case 'moderate': return 'Moderate';
  1206→    case 'severe': return 'Severe';
  1207→    default: return 'Unknown';
  1208→  }
  1209→}
