  2028→
  2029→// ============================================
  2030→// CLASSIFICATION FUNCTIONS
  2031→// ============================================
  2032→
  2033→/**
  2034→ * Get weakness severity based on average score
  2035→ * 0-3 = "Severe", 3-5 = "Moderate", 5-7 = "Minor"
  2036→ */
  2037→function getWeaknessSeverity(avgScore: number): WeaknessSeverity {
  2038→  if (avgScore <= 3) return 'severe';
  2039→  if (avgScore <= 5) return 'moderate';
  2040→  return 'minor';
  2041→}
  2042→
  2043→/**
  2044→ * Get strength grade based on average score
  2045→ * 8-9 = "Good", 9-9.5 = "Excellent", 9.5-10 = "Ideal"
  2046→ */
  2047→function getStrengthGrade(avgScore: number): StrengthGrade {
  2048→  if (avgScore >= 9.5) return 'ideal';
  2049→  if (avgScore >= 9) return 'excellent';
  2050→  return 'good';
  2051→}
  2052→
  2053→/**
  2054→ * Find metric by display name from measurements array
  2055→ */
  2056→function findMetricByName(
  2057→  metricName: string,
  2058→  measurements: FaceIQScoreResult[]
  2059→): FaceIQScoreResult | undefined {
  2060→  // First try direct ID mapping
  2061→  const metricId = METRIC_NAME_TO_ID[metricName];
  2062→  if (metricId) {
  2063→    return measurements.find(m => m.metricId === metricId);
  2064→  }
  2065→
  2066→  // Fallback: try fuzzy matching on name
  2067→  const normalizedName = metricName.toLowerCase().replace(/[^a-z0-9]/g, '');
  2068→  return measurements.find(m => {
  2069→    const normalizedMetricName = m.name.toLowerCase().replace(/[^a-z0-9]/g, '');
  2070→    return normalizedMetricName.includes(normalizedName) ||
  2071→           normalizedName.includes(normalizedMetricName);
  2072→  });
  2073→}
  2074→
  2075→/**
  2076→ * Check if a metric should be considered a "false positive" weakness.
  2077→ * This handles directional/dimorphic metrics where low scores don't mean weakness.
  2078→ *
  2079→ * Example: Canthal Tilt of 3° gets a low score (not in 6-8° ideal range)
  2080→ * but is still a POSITIVE trait, not a weakness.
  2081→ */
  2082→function isFalsePositiveWeakness(metric: MatchedMetric): boolean {
  2083→  const config = FACEIQ_METRICS[metric.metricId];
  2084→  if (!config) return false;
  2085→
  2086→  // Check if the value is actually acceptable based on polarity
  2087→  const { acceptable } = isValueAcceptable(metric.value, config);
  2088→  return acceptable;
  2089→}
  2090→
  2091→/**
  2092→ * Process all insight definitions against user's measurements
  2093→ */
  2094→export function classifyInsights(
  2095→  measurements: FaceIQScoreResult[],
  2096→  insightDefinitions: InsightDefinition[] = INSIGHTS_DEFINITIONS
  2097→): InsightClassificationResult {
  2098→  const strengths: ClassifiedStrength[] = [];
  2099→  const weaknesses: ClassifiedWeakness[] = [];
  2100→
  2101→  for (const insight of insightDefinitions) {
  2102→    const { metrics, threshold } = insight.severity_logic;
  2103→
  2104→    // Find matching measurements for this insight
  2105→    const matchedMetrics: MatchedMetric[] = [];
  2106→    for (const metricName of metrics) {
  2107→      const measurement = findMetricByName(metricName, measurements);
  2108→      if (measurement) {
  2109→        matchedMetrics.push({
  2110→          metricId: measurement.metricId,
  2111→          metricName: measurement.name,
  2112→          score: measurement.score,
  2113→          value: measurement.value,
  2114→          idealMin: measurement.idealMin,
  2115→          idealMax: measurement.idealMax,
  2116→          unit: measurement.unit,
  2117→          category: measurement.category,
  2118→        });
  2119→      }
  2120→    }
  2121→
  2122→    // Skip if no metrics matched (need at least 1)
  2123→    if (matchedMetrics.length === 0) continue;
  2124→
  2125→    // Calculate average score
  2126→    const avgScore = matchedMetrics.reduce((sum, m) => sum + m.score, 0) / matchedMetrics.length;
  2127→
  2128→    // Classify based on type and threshold
  2129→    if (insight.type === 'weakness') {
  2130→      // Weakness: avg < threshold → add to areas of improvement
  2131→      if (avgScore < threshold) {
  2132→        // ========== FALSE POSITIVE CHECK ==========
  2133→        // Before adding to weaknesses, check if ANY of the matched metrics
  2134→        // are actually in an "acceptable" zone based on their polarity.
  2135→        // If so, this is a false positive and should be skipped or reclassified.
  2136→        const falsePositiveMetrics = matchedMetrics.filter(isFalsePositiveWeakness);
  2137→
  2138→        // If ALL matched metrics are false positives, skip this weakness entirely
  2139→        if (falsePositiveMetrics.length === matchedMetrics.length) {
  2140→          // This is a false positive weakness - the values are actually acceptable
  2141→          // Consider adding to strengths if scores are decent
  2142→          if (avgScore >= 6) {
  2143→            strengths.push({
  2144→              insightId: `${insight.id}_acceptable`,
  2145→              title: insight.title.replace('Negative', 'Neutral').replace('Weak', 'Moderate'),
  2146→              description: `While not in the peak ideal range, the measured values are within acceptable limits.`,
  2147→              grade: 'good',
  2148→              gradeLabel: 'Acceptable',
  2149→              avgScore,
  2150→              matchedMetrics,
  2151→            });
  2152→          }
  2153→          continue; // Skip adding as weakness
  2154→        }
  2155→
  2156→        // If SOME metrics are false positives, filter them out
  2157→        const trueWeaknessMetrics = matchedMetrics.filter(m => !isFalsePositiveWeakness(m));
  2158→        if (trueWeaknessMetrics.length === 0) continue;
  2159→
  2160→        // Recalculate average with only true weakness metrics
  2161→        const trueAvgScore = trueWeaknessMetrics.reduce((sum, m) => sum + m.score, 0) / trueWeaknessMetrics.length;
  2162→
  2163→        // Only add as weakness if true average is still below threshold
  2164→        if (trueAvgScore < threshold) {
  2165→          const severity = getWeaknessSeverity(trueAvgScore);
  2166→          const labels = insight.content.severity_labels;
  2167→          const severityLabel = labels?.[severity] || severity;
  2168→
  2169→          weaknesses.push({
  2170→            insightId: insight.id,
  2171→            title: insight.title,
  2172→            description: insight.content.description,
  2173→            severity,
  2174→            severityLabel,
  2175→            avgScore: trueAvgScore,
  2176→            matchedMetrics: trueWeaknessMetrics,
  2177→          });
  2178→        }
  2179→      }
  2180→    } else {
  2181→      // Strength: avg > threshold → add to key strengths
  2182→      if (avgScore > threshold) {
  2183→        const grade = getStrengthGrade(avgScore);
  2184→        const labels = insight.content.grade_labels;
  2185→        const gradeLabel = labels?.[grade] || grade;
  2186→
  2187→        strengths.push({
  2188→          insightId: insight.id,
  2189→          title: insight.title,
  2190→          description: insight.content.description,
  2191→          grade,
  2192→          gradeLabel,
  2193→          avgScore,
  2194→          matchedMetrics,
  2195→        });
  2196→      }
  2197→    }
  2198→  }
  2199→
  2200→  // Sort by score (weaknesses by lowest score first, strengths by highest first)
  2201→  weaknesses.sort((a, b) => a.avgScore - b.avgScore);
  2202→  strengths.sort((a, b) => b.avgScore - a.avgScore);
  2203→
  2204→  return { strengths, weaknesses };
  2205→}
  2206→
  2207→/**
  2208→ * Convert ClassifiedStrength to Strength type for UI compatibility
  2209→ */
  2210→export function convertToStrength(classified: ClassifiedStrength): Strength {
  2211→  return {
  2212→    id: `insight_strength_${classified.insightId}`,
  2213→    strengthName: `${classified.title} (${classified.gradeLabel})`,
  2214→    summary: classified.description,
  2215→    avgScore: classified.avgScore,
  2216→    qualityLevel: classified.grade === 'ideal' ? 'ideal' :
  2217→                  classified.grade === 'excellent' ? 'excellent' : 'good',
  2218→    categoryName: classified.matchedMetrics[0]?.category || 'General',
  2219→    responsibleRatios: classified.matchedMetrics.map(m => ({
  2220→      ratioName: m.metricName,
  2221→      ratioId: m.metricId,
  2222→      score: m.score,
  2223→      value: m.value,
  2224→      idealMin: m.idealMin,
  2225→      idealMax: m.idealMax,
  2226→      unit: m.unit,
  2227→      category: m.category,
  2228→    })),
  2229→  };
  2230→}
  2231→
  2232→/**
  2233→ * Convert ClassifiedWeakness to Flaw type for UI compatibility
  2234→ */
  2235→export function convertToFlaw(classified: ClassifiedWeakness, index: number, rollingLost: number): Flaw {
  2236→  const impact = (10 - classified.avgScore) * 0.5 * classified.matchedMetrics.length;
  2237→  const newRollingLost = rollingLost + impact;
  2238→
  2239→  return {
  2240→    id: `insight_flaw_${classified.insightId}`,
  2241→    flawName: `${classified.title} (${classified.severityLabel})`,
  2242→    summary: classified.description,
  2243→    harmonyPercentageLost: impact,
  2244→    standardizedImpact: impact / 10,
  2245→    categoryName: classified.matchedMetrics[0]?.category || 'General',
  2246→    responsibleRatios: classified.matchedMetrics.map(m => ({
  2247→      ratioName: m.metricName,
  2248→      ratioId: m.metricId,
  2249→      score: m.score,
  2250→      value: m.value,
  2251→      idealMin: m.idealMin,
  2252→      idealMax: m.idealMax,
  2253→      unit: m.unit,
  2254→      category: m.category,
  2255→    })),
  2256→    rollingPointsDeducted: newRollingLost,
  2257→    rollingHarmonyPercentageLost: newRollingLost,
  2258→    rollingStandardizedImpact: newRollingLost / 10,
  2259→  };
  2260→}
  2261→
  2262→/**
  2263→ * Main function: Generate strengths and flaws from measurements using insights
  2264→ */
  2265→export function generateInsightBasedResults(
  2266→  measurements: FaceIQScoreResult[]
  2267→): { strengths: Strength[]; flaws: Flaw[] } {
  2268→  const { strengths: classifiedStrengths, weaknesses: classifiedWeaknesses } = classifyInsights(measurements);
  2269→
  2270→  // Convert to UI-compatible types
  2271→  const strengths = classifiedStrengths.map((s) => convertToStrength(s));
  2272→
  2273→  let rollingLost = 0;
  2274→  const flaws = classifiedWeaknesses.map((w, i) => {
  2275→    const flaw = convertToFlaw(w, i, rollingLost);
  2276→    rollingLost = flaw.rollingPointsDeducted || 0;
  2277→    return flaw;
  2278→  });
  2279→
  2280→  return { strengths, flaws };
  2281→}
